export const handleSelectField = component => event => {
  const value = event && event.nativeEvent ? event.target.value : event
  component.setState({
    credentials: value,
    fielPassword: '',
    ciecUser: '',
    ciecPassword: '',
    fiel: {
      attachedKeyFile: '',
      originalKeyFilename: '',
      contentKeyType: '',
      attachedCerFile: '',
      originalCeryFilename: '',
      contentCerType: ''
    }
  })
}

export const createAccount = ({
  fullName,
  phone,
  email,
  password,
  passwordConfirmation,
  rfc,
  params
}) => ({
  user: {
    full_name: fullName,
    phone: phone,
    email: email,
    password: password,
    password_confirmation: passwordConfirmation
  },
  taxable_entity: {
    rfc: rfc
  },
  utms: {
    utm_source: params.utm_source || 'Directo',
    utm_campaign: params.utm_campaign || 'Organico',
    come_from: params.come_from || 'Directo',
    landing: params.landing || window.location.href
  }
})

export const uploadFiel = ({ fiel, fielPassword }) => ({
  taxable_entity: {
    fiel_cer_file: {
      attached_file: fiel.attachedCerFile,
      content_type: fiel.contentCerType,
      original_filename: fiel.originalCerFilename
    },
    fiel_key_file: {
      attached_file: fiel.attachedKeyFile,
      content_type: fiel.contentKeyType,
      original_filename: fiel.originalKeyFilename
    },
    fiel_key_file_password: fielPassword
  }
})

export const uploadCiec = ({ ciecPassword }) => ({
  taxable_entity: {
    sat_password: ciecPassword
  }
})
