import React, { Component } from 'react'
import classNames from 'classnames'
import clipboard from 'clipboard-polyfill'
import update from 'immutability-helper'
import validator from 'validator'

import NotificationsActions from 'actions/notifications'
import SatCredentialsActions from 'actions/satCredentials'
import SessionStore from 'stores/session'
import SatCredentialsStore from 'stores/satCredentials'
import SelectFile from 'shared/selectFile'
import TextField from 'shared/textField'
import Button from 'shared/button'

function getCurrentState () {
  let currentEntity = SessionStore.getCurrentEntity()
  let credentials = SatCredentialsStore.getCredentials()
  const user = SessionStore.getUser()
  return {
    currentEntity,
    credentials,
    user
  }
}

class Fiel extends Component {
  constructor () {
    super()
    this.data = {
      requestInProgress: false,
      isFielKeyModalOpen: false,
      isFielCerModalOpen: false,
      fiel: {
        fiel_key_file_password: '',
        fiel_key_file: {
          attached_file: '',
          original_filename: '',
          content_type: ''
        },
        fiel_cer_file: {
          attached_file: '',
          original_filename: '',
          content_type: ''
        }
      },
      isValid: {
        fiel_key_file_password: true,
        fiel_key_file: true,
        fiel_cer_file: true
      }
    }
    this.state = Object.assign({}, this.data, getCurrentState())
    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    SatCredentialsStore.addChangeListener(this._onChange)
  }

  componentWillUnmount () {
    // Removes the listener to avoid memory leaks
    SatCredentialsStore.removeChangeListener(this._onChange)
  }

  _onChange () {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    let state = getCurrentState()
    this.setState(state)
  }

  // Persist password to state
  onChangeFielPassword (e) {
    this.setState({
      fiel: update(this.state.fiel, {
        fiel_key_file_password: { $set: e.target.value }
      })
    })
  }

  // Toggle key modal
  toggleFielKeyModal () {
    this.setState({ isFielKeyModalOpen: !this.state.isFielKeyModalOpen })
  }

  // Toggle certificate modal
  toggleFielCerModal () {
    this.setState({ isFielCerModalOpen: !this.state.isFielCerModalOpen })
  }

  // Persist key to state
  setFielKey (file, filename, mime) {
    let key = {
      attached_file: file,
      original_filename: filename,
      content_type: mime
    }
    this.setState({
      fiel: update(this.state.fiel, { fiel_key_file: { $set: key } })
    })
  }

  // Persist certificate to state
  setFielCer (file, filename, mime) {
    let cer = {
      attached_file: file,
      original_filename: filename,
      content_type: mime
    }
    this.setState({
      fiel: update(this.state.fiel, { fiel_cer_file: { $set: cer } })
    })
  }

  // Validate non empty key
  isValidFielKey () {
    let isValid = !validator.isEmpty(
      this.state.fiel.fiel_key_file.attached_file
    )
    this.setState({
      isValid: update(this.state.isValid, { fiel_key_file: { $set: isValid } })
    })
    return isValid
  }

  // Validate non empty certificate
  isValidFielCer () {
    let isValid = !validator.isEmpty(
      this.state.fiel.fiel_cer_file.attached_file
    )
    this.setState({
      isValid: update(this.state.isValid, { fiel_cer_file: { $set: isValid } })
    })
    return isValid
  }

  // Validate non empty password
  isValidFielPassword () {
    let isValid = !validator.isEmpty(this.state.fiel.fiel_key_file_password)
    this.setState({
      isValid: update(this.state.isValid, {
        fiel_key_file_password: { $set: isValid }
      })
    })
    return isValid
  }

  uploadFiel (e) {
    e.preventDefault()
    if (
      this.isValidFielKey() &&
      this.isValidFielCer() &&
      this.isValidFielPassword()
    ) {
      this.setState({ requestInProgress: true }, () => {
        SatCredentialsActions.updateSatCredentials({
          taxable_entity: this.state.fiel
        })
          .then(() => {
            NotificationsActions.success('FIEL guardada correctamente')
            this.setState({ requestInProgress: false })
            SatCredentialsActions.getSatCredentials()
          })
          .catch(error => {
            NotificationsActions.error(error)
            this.setState({ requestInProgress: false })
          })
      })
    }
  }

  render () {
    const { user } = this.state
    return (
      <div>
        <h5 className='subtitle is-6 is-marginless'>FIEL</h5>
        <div className='columns'>
          <div className='column'>
            <div className='field'>
              <TextField
                label='Contraseña de la FIEL'
                type='password'
                placeholder='Contraseña de la FIEL'
                className={classNames('input', {
                  'is-danger': !this.state.isValid.fiel_key_file_password
                })}
                onChange={this.onChangeFielPassword.bind(this)}
                value={this.state.fiel.fiel_key_file_password}
              />
              {user.role !== 'sales' && (
                <a
                  onClick={() => {
                    clipboard
                      .writeText(this.state.credentials.fiel.password)
                      .then(
                        NotificationsActions.success(
                          'Contraseña copiada al portapapeles'
                        )
                      )
                  }}
                  className={classNames({
                    'is-hidden': !this.state.credentials.fiel.password
                  })}
                >
                  Copiar contraseña almacenada
                </a>
              )}
            </div>
          </div>
          <div className='column'>
            <div className='field'>
              <label className='caption'>Extensión .key</label>
              <Button
                buttonStyle='info'
                onClick={this.toggleFielKeyModal.bind(this)}
                className='is-fullwidth'
                icon={
                  this.state.fiel.fiel_key_file.original_filename
                    ? 'file-outline'
                    : 'plus'
                }
              >
                {this.state.fiel.fiel_key_file.original_filename ||
                  'Seleccionar Archivo'}
              </Button>
              <p
                className={classNames('help is-danger', {
                  'is-hidden': this.state.isValid.fiel_key_file
                })}
              >
                * Campo requerido
              </p>
              {user.role !== 'sales' && (
                <a
                  className={classNames({
                    'is-hidden': !this.state.credentials.fiel.key_file
                  })}
                  href={this.state.credentials.fiel.key_file}
                  download
                >
                  Descargar archivo .key almacenado
                </a>
              )}
            </div>
          </div>
          <div className='column'>
            <div className='field'>
              <label className='caption'>Extensión .cer</label>
              <Button
                buttonStyle='info'
                className='is-fullwidth'
                onClick={this.toggleFielCerModal.bind(this)}
                icon={
                  this.state.fiel.fiel_cer_file.original_filename
                    ? 'file-outline'
                    : 'plus'
                }
              >
                {this.state.fiel.fiel_cer_file.original_filename ||
                  'Seleccionar Archivo'}
              </Button>
              <p
                className={classNames('help is-danger', {
                  'is-hidden': this.state.isValid.fiel_cer_file
                })}
              >
                * Campo requerido
              </p>
              {user.role !== 'sales' && (
                <a
                  className={classNames({
                    'is-hidden': !this.state.credentials.fiel.cer_file
                  })}
                  href={this.state.credentials.fiel.cer_file}
                  download
                >
                  Descargar archivo .cer almacenado
                </a>
              )}
            </div>
          </div>
        </div>
        <Button
          className='align-right'
          buttonStyle='primary'
          icon='content-save'
          loading={this.state.requestInProgress}
          onClick={this.uploadFiel.bind(this)}
        >
          Guardar FIEL
        </Button>
        <SelectFile
          isActive={this.state.isFielKeyModalOpen}
          toggleModal={this.toggleFielKeyModal.bind(this)}
          title='Archivo .key de la FIEL'
          fileTypePlaceholder='Extensión .key de la FIEL'
          setFile={this.setFielKey.bind(this)}
          accept={'.key'}
        />
        <SelectFile
          isActive={this.state.isFielCerModalOpen}
          toggleModal={this.toggleFielCerModal.bind(this)}
          title='Archivo .cer de la FIEL'
          fileTypePlaceholder='Extensión .cer de la FIEL'
          setFile={this.setFielCer.bind(this)}
          accept={'.cer'}
        />
      </div>
    )
  }
}

export default Fiel
