import React, { Component } from 'react'
import { Link } from 'react-router'
import classNames from 'classnames'
import update from 'immutability-helper'

import TextField from 'shared/textField'
import SelectField from 'shared/selectField'
import Button from 'shared/button'

const STATUS_ACTIVE = 'active'
const STATUS_INACTIVE = 'inactive'
const STATUS_ACTIVE_DISPLAY = 'Activo'
const STATUS_INACTIVE_DISPLAY = 'Inactivo'
const STATUS_ACTIVE_DISPLAY_ACTION = 'Desactivar'
const STATUS_INACTIVE_DISPLAY_ACTION = 'Activar'
const STATUS = [
  { id: STATUS_ACTIVE, label: STATUS_ACTIVE_DISPLAY },
  { id: STATUS_INACTIVE, label: STATUS_INACTIVE_DISPLAY }
]

const ROLE_ACCOUNTANT = 'accountant'
const ROLE_LEADER = 'leader'
const ROLE_ACCOUNTANT_DISPLAY = 'Contador'
const ROLE_LEADER_DISPLAY = 'Líder de equipo'
const ROLE = [
  { id: ROLE_ACCOUNTANT, label: ROLE_ACCOUNTANT_DISPLAY },
  { id: ROLE_LEADER, label: ROLE_LEADER_DISPLAY }
]

/*
 * Accountants is the view in /admin/contadores
 */

class Accountants extends Component {
  constructor (props) {
    super(props)
    this.data = {
      accountants: [],
      filters: {
        userID: '',
        email: '',
        name: '',
        status: STATUS_ACTIVE,
        role: ROLE_ACCOUNTANT
      }
    }
    this.state = Object.assign({}, this.data)
  }

  // Load data after mounting
  componentDidMount () {
    this.loadData()
  }

  // Get a list of accountants
  loadData () {
    this.setState({
      accountants: [
        {
          userID: 1,
          email: 'correo123@hola.com',
          name: 'Nombre de una persona',
          lastname: 'Y su apellido',
          status: STATUS_ACTIVE,
          kind: 'accountant'
        },
        {
          userID: 1,
          email: 'correo123@hola.com',
          name: 'Nombre de una persona',
          lastname: 'Y su apellido',
          status: STATUS_ACTIVE,
          kind: 'accountant'
        },
        {
          userID: 1,
          email: 'correo123@hola.com',
          name: 'Nombre de una persona',
          lastname: 'Y su apellido',
          status: STATUS_INACTIVE,
          kind: 'accountant'
        },
        {
          userID: 1,
          email: 'correo123@hola.com',
          name: 'Nombre de una persona',
          lastname: 'Y su apellido',
          status: STATUS_INACTIVE,
          kind: 'accountant'
        },
        {
          userID: 1,
          email: 'correo123@hola.com',
          name: 'Nombre de una persona',
          lastname: 'Y su apellido',
          status: STATUS_ACTIVE,
          kind: 'accountant'
        },
        {
          userID: 1,
          email: 'correo123@hola.com',
          name: 'Nombre de una persona',
          lastname: 'Y su apellido',
          status: STATUS_INACTIVE,
          kind: 'accountant'
        }
      ]
    })
  }

  // Sets the value of this.state.filters.userID with the target value
  onChangeFiltersUserID (e) {
    this.setState({
      filters: update(this.state.filters, { userID: { $set: e.target.value } })
    })
  }

  // Sets the value of this.state.filters.email with the target value
  onChangeFiltersEmail (e) {
    this.setState({
      filters: update(this.state.filters, { email: { $set: e.target.value } })
    })
  }

  // Sets the value of this.state.filters.name with the target value
  onChangeFiltersName (e) {
    this.setState({
      filters: update(this.state.filters, { name: { $set: e.target.value } })
    })
  }

  // Sets the value of this.state.filters.status with the target value
  setFilterStatus (e) {
    e.preventDefault()
    this.setState({
      filters: update(this.state.filters, { status: { $set: e.target.value } })
    })
  }

  // Sets the value of this.state.filters.role with the target value
  setFilterRole (e) {
    e.preventDefault()
    this.setState({
      filters: update(this.state.filters, { role: { $set: e.target.value } })
    })
  }

  // Will filter the results, currently only prints the selected filter values
  filterResults (e) {
    e.preventDefault()
    console.log(this.state.filters)
  }

  // Renders the main button (button at the right side of the title)
  renderNewButton (right) {
    return (
      <p className={classNames('control', { 'is-pulled-right': right })}>
        <Link to='/admin/contadores/nuevo'>
          <Button buttonStyle='primary'>Nuevo contador</Button>
        </Link>
      </p>
    )
  }

  // Renders the filter fields (inputs, selects and search button)
  renderFilters () {
    return (
      <div className='box'>
        <div className='columns'>
          <strong className='side-padding'>Filtrar por:</strong>
        </div>
        <div className='columns'>
          <div className='column'>
            <TextField
              placeholder='Usuario'
              value={this.state.filters.userID}
              onChange={this.onChangeFiltersUserID.bind(this)}
            />
          </div>
          <div className='column'>
            <TextField
              placeholder='Correo'
              value={this.state.filters.email}
              onChange={this.onChangeFiltersEmail.bind(this)}
            />
          </div>
          <div className='column'>
            <TextField
              placeholder='Nombre o Apellido'
              value={this.state.filters.name}
              onChange={this.onChangeFiltersName.bind(this)}
            />
          </div>
          <div className='column'>
            <SelectField
              options={STATUS}
              placeholder='Estatus'
              value={this.state.filters.status}
              onChange={this.setFilterStatus.bind(this)}
            />
          </div>
          <div className='column'>
            <SelectField
              options={ROLE}
              placeholder='Rol'
              value={this.state.filters.role}
              onChange={this.setFilterRole.bind(this)}
            />
          </div>
          <div className='column is-3-desktop is-2-widescreen'>
            <div className='columns is-mobile'>
              <div className='column'>
                <Button
                  icon='close-circle-outline'
                  // onClick={this.onCleanFilters.bind(this)}
                >
                  Limpiar
                </Button>
              </div>
              <div className='column'>
                <Button
                  buttonStyle='infoInv'
                  icon='magnify'
                  onClick={this.filterResults.bind(this)}
                >
                  Filtrar
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }

  // Renders the table and fills it with data
  renderTable () {
    let renderTable = (
      <div className='content'>
        <p>No hay rubros</p>
        {this.renderNewButton()}
      </div>
    )
    if (this.state.accountants.length > 0) {
      let accountants = this.state.accountants.map(function (accountant, index) {
        let statusLabel = STATUS_ACTIVE_DISPLAY
        let statusAction = STATUS_ACTIVE_DISPLAY_ACTION
        if (accountant.status === STATUS_INACTIVE) {
          statusLabel = STATUS_INACTIVE_DISPLAY
          statusAction = STATUS_INACTIVE_DISPLAY_ACTION
        }
        let renderStatus = (
          <p className='control table--actions'>
            <span className='with-tooltip'>
              <span className='tooltip'>{statusAction}</span>
              {statusLabel}
            </span>
          </p>
        )

        return (
          <tr
            key={accountant.userID}
            className={classNames({
              'row--inactive': accountant.status === STATUS_INACTIVE
            })}
          >
            <td>{accountant.userID}</td>
            <td>{accountant.email}</td>
            <td>{accountant.name}</td>
            <td>{accountant.lastname}</td>
            <td>{renderStatus}</td>
            <td>{accountant.kind}</td>
            <td>
              <p className='control table--actions'>
                <Link to={'/admin/contadores/editar/' + accountant.userID}>
                  <span className='with-tooltip'>
                    <span className='tooltip'>Editar</span>
                    <i className='mdi mdi-pencil' aria-hidden='true' />
                  </span>
                </Link>
                <span className='with-tooltip'>
                  <span className='tooltip'>Enviar correo de activación</span>
                  <i className='mdi mdi-email' aria-hidden='true' />
                </span>
              </p>
            </td>
          </tr>
        )
      })
      renderTable = (
        <div>
          <table className='table'>
            <thead>
              <tr>
                <th className='is-unselectable cursor-default'>Usuario</th>
                <th className='is-unselectable cursor-default'>Correo</th>
                <th className='is-unselectable cursor-default'>Nombre</th>
                <th className='is-unselectable cursor-default'>Apellido</th>
                <th className='is-unselectable cursor-default'>Estado</th>
                <th className='is-unselectable cursor-default'>Rol</th>
                <th className='is-unselectable cursor-default'>Acciones</th>
              </tr>
            </thead>
            <tbody>{accountants}</tbody>
          </table>
          {this.renderNewButton()}
        </div>
      )
    }
    return renderTable
  }

  // Main render
  render () {
    return (
      <div>
        <h1 className='title is-4'>
          Accountants
          {this.renderNewButton('right')}
        </h1>
        {this.renderFilters()}
        {this.renderTable()}
      </div>
    )
  }
}

export default Accountants
