// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import accounting from 'accounting'
import { browserHistory, Link } from 'react-router'
import AppSignal from 'appsignal'

// Components
import Button from 'shared/button'
import API from 'api'
import Card from 'shared/card'
import NotificationsActions from 'actions/notifications'
import { INVOICING_STATUS } from 'shared/catalogs'

// Children
import Sales from './../regular'
import SalesThirdParty from './../a-terceros'

// Keys
const INVOICES_COUNT_KEY = 'invoicesCount'
const INVOICES_KEY = 'invoice_import'
const INVOICES_STAMPING_FUNCTIONS = [
  { id: 'regular', function: API.MultipleInvoicing.StampRegular },
  { id: 'a_terceros', function: API.MultipleInvoicing.StampThirdParty }
]

// Count invalid invoices, an invoice is invalid if it has the top-level property invalid set to true
const countValidInvoices = invoices => {
  let invalidInvoices = invoices.filter(i => i.invalid).length
  return {
    valid: invoices.length - invalidInvoices,
    invalid: invalidInvoices
  }
}

class MultipleInvoicingProcess extends Component {
  constructor (props) {
    super(props)
    this.data = {
      [INVOICES_COUNT_KEY]: 0,
      [INVOICES_KEY]: [],
      invoicingStatus: 'processing'
    }
    this.state = Object.assign({}, this.data)
  }

  // Load data on component mount
  componentDidMount () {
    this.loadData()
  }

  // Download process info
  loadData () {
    API.MultipleInvoicing.Show(this.props.params.id)
      .then(r => {
        const invoices =
          typeof r[INVOICES_KEY] === 'string'
            ? JSON.parse(r[INVOICES_KEY])
            : r[INVOICES_KEY]
        this.setState({
          [INVOICES_KEY]: invoices || [],
          invoicingStatus: r.status
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
        NotificationsActions.error(error)
      })
  }

  // Call the stamping endpoint for this batch of invoices
  stampInvoices () {
    INVOICES_STAMPING_FUNCTIONS.find(
      request => request.id === this.props.params['tab']
    )
      .function(this.props.params.id)
      .then(r => {
        NotificationsActions.success('Las facturas se están emitiendo')
        browserHistory.push('/facturacion_masiva')
      })
      .catch(error => {
        AppSignal.sendError(error)
        NotificationsActions.error(error)
      })
  }

  // Render every invoice
  renderTable (validInvoiceCount, tab) {
    let tableContent = null
    switch (tab) {
      case 'regular':
        tableContent = (
          <Sales
            invoices={this.state[INVOICES_KEY]}
            validInvoiceCount={validInvoiceCount}
          />
        )
        break
      case 'a_terceros':
        tableContent = (
          <SalesThirdParty
            invoices={this.state[INVOICES_KEY]}
            validInvoiceCount={validInvoiceCount}
          />
        )
        break
    }
    return this.state[INVOICES_KEY].length > 0 ? (
      <div>
        <Button
          icon='file-send'
          buttonStyle='primary'
          onClick={this.stampInvoices.bind(this)}
          disabled={
            this.state.invoicingStatus !== 'ready_to_stamp' ||
            validInvoiceCount === 0
          }
        >
          Facturar
        </Button>
        <br />
        <br />
        {tableContent}
      </div>
    ) : null
  }

  // Main render
  render () {
    let { valid, invalid } = countValidInvoices(this.state[INVOICES_KEY])
    let { tab } = this.props.params
    let validPlural = valid !== 1 ? 's' : null
    let invalidPlural = invalid !== 1 ? 's' : null
    return (
      <div>
        <div className='flex-between'>
          <h1 className='headline'>Detalle del proceso</h1>
          <div
            className='flex-column flex-end'
            style={{ display: 'inline-block' }}
          >
            <Link to={`/facturacion_masiva/${tab}`}>
              <Button icon='arrow-left' buttonStyle='white'>
                Regresar
              </Button>
            </Link>
          </div>
        </div>
        <Card className='is-fullheight flex-column'>
          <div className='content'>
            <div className='block unselectable'>
              <p>
                Estado de la facturación:{' '}
                {INVOICING_STATUS[this.state.invoicingStatus]}
              </p>
            </div>
          </div>
          {this.renderTable(valid, tab)}
          <div className='caption'>
            {accounting.formatNumber(valid)} factura
            {validPlural} válida
            {validPlural}
          </div>
          <div className='caption'>
            {accounting.formatNumber(invalid)} factura
            {invalidPlural} inválida
            {invalidPlural}
          </div>
        </Card>
      </div>
    )
  }
}

MultipleInvoicingProcess.propTypes = {
  params: PropTypes.shape({
    id: PropTypes.string,
    tab: PropTypes.number
  })
}

export default MultipleInvoicingProcess
