// Libraries
import React from 'react'
import PropTypes from 'prop-types'
import DocumentTitle from 'react-document-title'

// Components
import SessionActions from 'actions/session'
import Footer from './shared/footer'
import Terms from './terms'
import Privacy from './privacy'
import Cookies from './cookies'
import Arco from './arco'
import RefundPolicy from './refundPolicy'
import PublicMenu from 'shared/publicMenu'
import Card from 'shared/card'

const TITLES = {
  'terminos-y-condiciones': 'Términos y Condiciones',
  privacidad: 'Aviso de Privacidad',
  cookies: 'Política de Cookies',
  'solicitud-arco': 'Solicitud de derechos ARCO',
  devoluciones: 'Política de devoluciones'
}

const ENCONTA_LINK = () => (
  <a href='https://www.enconta.com'>https://www.enconta.com</a>
)

const content = {
  'terminos-y-condiciones': <Terms />,
  privacidad: <Privacy encontaLink={ENCONTA_LINK} />,
  cookies: <Cookies encontaLink={ENCONTA_LINK} />,
  'solicitud-arco': <Arco />,
  devoluciones: <RefundPolicy />
}

const About = props => {
  const hasSession = SessionActions.hasSession()
  return (
    <DocumentTitle title={`${TITLES[props.route.page]} - Enconta`}>
      <div id='legal-info'>
        <PublicMenu />
        <div id='main' className='about'>
          <div className='container'>
            <div className='flex-between'>
              <h1 className='headline'>Información Legal</h1>
              {hasSession ? (
                <div className='zendesk-link'>
                  <i className='mdi mdi-open-in-new' />
                  <a href='https://enconta.zendesk.com/hc/es' target='_blank'>
                    Ir al Centro de Ayuda
                  </a>
                </div>
              ) : null}
            </div>
            <Card
              title={[
                {
                  id: 'terminos-y-condiciones',
                  label: 'Términos y Condiciones'
                },
                { id: 'privacidad', label: 'Aviso de Privacidad' },
                { id: 'cookies', label: 'Política de Cookies' },
                { id: 'solicitud-arco', label: 'Solicitud de derechos ARCO' },
                { id: 'devoluciones', label: 'Política de devoluciones' }
              ]}
              activeTab={props.route.page}
              baseUrl='/acerca-de'
            >
              <div className='about-card-content'>
                {content[props.route.page]}
              </div>
            </Card>
          </div>
        </div>
        <Footer />
      </div>
    </DocumentTitle>
  )
}

About.propTypes = {
  route: PropTypes.shape({
    page: PropTypes.number
  })
}

export default About
