// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import update from 'immutability-helper'
import classNames from 'classnames'
import moment from 'moment'
import DatePicker from 'react-datepicker'
import { Async } from 'react-select'
import _Nil from 'lodash/isNil'
import AppSignal from 'appsignal'

// Components
import API from 'api'
import { countDays, formatDateInvoice } from 'utils'

// Employee keys
const EMPLOYEE_KEY = 'employee'

// Payroll keys
const PAYROLL_KEY = 'payroll_mg'
const PAYROLL_VERSION_KEY = 'version'
const PAYROLL_TYPE_KEY = 'payroll_type'
const PAYROLL_DAYS_KEY = 'paid_days'
const PAYROLL_START_DATE_KEY = 'starting_payment_period'
const PAYROLL_END_DATE_KEY = 'ending_payment_period'
const PAYROLL_PAYMENT_DATE_KEY = 'payment_date'

const FULL_NAME_KEY = 'full_name'

const defaultPayroll = {
  [PAYROLL_VERSION_KEY]: '1.2',
  [PAYROLL_TYPE_KEY]: 'O',
  [PAYROLL_START_DATE_KEY]: moment(),
  [PAYROLL_END_DATE_KEY]: moment(),
  [PAYROLL_DAYS_KEY]: 1,
  [PAYROLL_PAYMENT_DATE_KEY]: moment()
}

class Employee extends Component {
  constructor () {
    super()
    this.data = {
      [EMPLOYEE_KEY]: null,
      [PAYROLL_KEY]: defaultPayroll,
      isValid: {
        [EMPLOYEE_KEY]: true
      }
    }
    this.state = Object.assign({}, this.data)
  }

  // Get clients for current taxable entity
  getEmployees (input) {
    return API.EditClient.GetEmployeesList({
      [FULL_NAME_KEY]: input,
      deactivated: 'false'
    })
      .then(response => {
        this.setState({ employees: response.employees })
        return { options: this.parseEmployees(response.employees) }
      })
      .catch(error => {
        AppSignal.sendError(error)
        throw error
      })
  }

  // Convert an array of employees in a format that react-select will understand
  parseEmployees (employees) {
    return employees
      .map(employee => ({
        value: employee.id,
        label: employee[FULL_NAME_KEY]
      }))
      .filter(employee => employee.label)
  }

  // Export payroll data
  exportPayroll () {
    return Object.assign({}, this.state[PAYROLL_KEY], {
      [PAYROLL_PAYMENT_DATE_KEY]: formatDateInvoice(
        this.state[PAYROLL_KEY][PAYROLL_PAYMENT_DATE_KEY]
      ),
      [PAYROLL_START_DATE_KEY]: formatDateInvoice(
        this.state[PAYROLL_KEY][PAYROLL_START_DATE_KEY]
      ),
      [PAYROLL_END_DATE_KEY]: formatDateInvoice(
        this.state[PAYROLL_KEY][PAYROLL_END_DATE_KEY]
      )
    })
  }

  // Export employee data
  exportEmployee () {
    if (this.state[EMPLOYEE_KEY] && !_Nil(this.state[EMPLOYEE_KEY].value)) {
      return {
        id: this.state[EMPLOYEE_KEY].value,
        cfdi_usage: 'P01'
      }
    }

    return null
  }

  // Persist selected employee to state
  setEmployee (employee) {
    this.setState({ [EMPLOYEE_KEY]: employee }, this.isValidEmployee)
  }

  // Return false if employee is not set on state
  isValidEmployee () {
    this.setState({
      isValid: update(this.state.isValid, {
        [EMPLOYEE_KEY]: { $set: !!this.state[EMPLOYEE_KEY] }
      })
    })
    const isValidEmployee = !!this.state[EMPLOYEE_KEY]
    if (!isValidEmployee) {
      this.props.onValidationError('empleado - campo requerido')
    }
    return isValidEmployee
  }

  // Handle changes to payroll startDate, counts days between payroll start and end date
  onChangeStartDate (e) {
    let payroll = update(this.state[PAYROLL_KEY], {
      [PAYROLL_START_DATE_KEY]: { $set: e }
    })
    payroll = update(payroll, {
      [PAYROLL_DAYS_KEY]: {
        $set: countDays(e, this.state[PAYROLL_KEY][PAYROLL_END_DATE_KEY]) + 1
      }
    })
    this.setState({ [PAYROLL_KEY]: payroll })
  }

  // Handle changes to payroll endDate, counts days between payroll start and end date
  onChangeEndDate (e) {
    let payroll = update(this.state[PAYROLL_KEY], {
      [PAYROLL_END_DATE_KEY]: { $set: e }
    })
    payroll = update(payroll, {
      [PAYROLL_DAYS_KEY]: {
        $set: countDays(this.state[PAYROLL_KEY][PAYROLL_START_DATE_KEY], e) + 1
      }
    })
    this.setState({ [PAYROLL_KEY]: payroll })
  }

  // Handle changes to payroll field
  onChangePayroll (field, e) {
    this.setState({
      [PAYROLL_KEY]: update(this.state[PAYROLL_KEY], {
        [field]: {
          $set: field === PAYROLL_PAYMENT_DATE_KEY ? e : e.target.value
        }
      })
    })
  }

  // Render employee select field and data if an employee is selected
  renderEmployee () {
    let renderEmployee = null
    if (this.state[EMPLOYEE_KEY] && this.state.employees.length > 0) {
      let employee = this.state.employees.find(
        employee => employee.id === this.state[EMPLOYEE_KEY].value
      )
      let { [FULL_NAME_KEY]: fullName, rfc, curp } = employee
      renderEmployee = (
        <div className='card'>
          <header className='card-header'>
            {typeof fullName !== 'undefined' ? (
              <p className='card-header-title'>Nombre: {fullName}</p>
            ) : null}
          </header>
          <div className='card-content'>
            <div className='content'>
              {typeof rfc !== 'undefined' ? <p>RFC: {rfc}</p> : null}
              {typeof curp !== 'undefined' ? <p>CURP: {curp}</p> : null}
            </div>
          </div>
        </div>
      )
    }
    return (
      <div className='columns'>
        <div className='column'>
          <label className='caption'>Empleado *</label>
          <div
            className={classNames('control espacio-medium', {
              'select-required': !this.state[EMPLOYEE_KEY]
            })}
          >
            <Async
              value={this.state[EMPLOYEE_KEY]}
              loadOptions={this.getEmployees.bind(this)}
              clearable
              placeholder='Selecciona un Empleado'
              loadingPlaceholder='Buscando'
              searchPromptText='Teclear el nombre del empleado'
              noResultsText='No encontrado'
              onChange={this.setEmployee.bind(this)}
              onBlur={this.isValidEmployee.bind(this)}
              ignoreAccents={false}
            />
            <span
              className={classNames('help is-danger', {
                'is-hidden': this.state.isValid[EMPLOYEE_KEY]
              })}
            >
              Este campo es requerido
            </span>
          </div>
          {renderEmployee}
        </div>
      </div>
    )
  }

  // Main render
  render () {
    return (
      <div>
        <h2 className='subheader-green'>Empleado</h2>
        {this.renderEmployee()}
        <div className='columns'>
          <div className='column'>
            <div className='field'>
              <div className='columns'>
                <div className='column'>
                  <label className='caption'>Fecha inicial</label>
                  <div className='control'>
                    <DatePicker
                      className={classNames('input', {
                        'is-danger': moment(
                          this.state[PAYROLL_KEY][PAYROLL_START_DATE_KEY]
                        ).isAfter(this.state[PAYROLL_KEY][PAYROLL_END_DATE_KEY])
                      })}
                      selected={this.state[PAYROLL_KEY][PAYROLL_START_DATE_KEY]}
                      onChange={this.onChangeStartDate.bind(this)}
                      locale='es-MX'
                    />
                  </div>
                </div>
                <div className='column'>
                  <label className='caption'>Fecha final</label>
                  <div className='control'>
                    <DatePicker
                      className={classNames('input', {
                        'is-danger': moment(
                          this.state[PAYROLL_KEY][PAYROLL_START_DATE_KEY]
                        ).isAfter(this.state[PAYROLL_KEY][PAYROLL_END_DATE_KEY])
                      })}
                      selected={this.state[PAYROLL_KEY][PAYROLL_END_DATE_KEY]}
                      onChange={this.onChangeEndDate.bind(this)}
                      locale='es-MX'
                    />
                  </div>
                </div>
              </div>
              <div className='columns'>
                <div className='column'>
                  <label className='caption'>DÃ­as pagados</label>
                  <div className='control'>
                    <input
                      type='text'
                      className='input is-disabled'
                      value={this.state[PAYROLL_KEY][PAYROLL_DAYS_KEY]}
                      disabled
                    />
                  </div>
                </div>
                <div className='column'>
                  <label className='caption'>Fecha de pago</label>
                  <div className='control'>
                    <DatePicker
                      className='input'
                      selected={
                        this.state[PAYROLL_KEY][PAYROLL_PAYMENT_DATE_KEY]
                      }
                      onChange={this.onChangePayroll.bind(
                        this,
                        PAYROLL_PAYMENT_DATE_KEY
                      )}
                      locale='es-MX'
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

Employee.propTypes = {
  onValidationError: PropTypes.func
}

Employee.defaultProps = {
  onValidationError: () => {}
}

export default Employee
