import React, { Component } from 'react'
import TopbarStore from 'stores/topbar'

export default class Progressbar extends Component {
  constructor (props) {
    super(props)
    this.state = {
      loaderPercent: TopbarStore.getLoaderPercent()
    }

    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    TopbarStore.addChangeListener(this._onChange)
  }

  componentWillUnmount () {
    // Removes the listener to avoid memory leaks
    TopbarStore.removeChangeListener(this._onChange)
  }

  _onChange () {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    const loaderPercent = TopbarStore.getLoaderPercent()

    this.setState({ loaderPercent })
  }

  render () {
    const { loaderPercent } = this.state

    return (
      <div
        className='topbar__progress-loader-bar'
        style={{
          width: `${loaderPercent}%`,
          opacity: loaderPercent < 100 ? 1 : 0
        }}
      />
    )
  }
}
