import React from 'react'
import classNames from 'classnames'
import PropTypes from 'prop-types'

/**
 *
 * Switch button component
 * @param {*} props
 */
const SwitchButton = props => {
  const { text, handleSwitch, checked, active } = props
  return (
    <div
      className={classNames('btn__border', {
        'btn__border--active': active
      })}
    >
      <div className=''>
        <label htmlFor='status' className='btn btn--component'>
          <input
            className='btn__status'
            onChange={handleSwitch}
            type='checkbox'
            id='status'
            checked={checked}
          />
          <span className='btn__switch btn__switch--flex btn--round' />
        </label>
      </div>
      <span
        className={classNames('btn__text-container', {
          'btn__text-container--active': active
        })}
      >
        {text}
      </span>
    </div>
  )
}

export default SwitchButton

SwitchButton.defaultProps = {
  text: '',
  handleSwitch: () => {},
  checked: '',
  active: false
}

SwitchButton.propTypes = {
  text: PropTypes.string,
  handleSwitch: PropTypes.func.isRequired,
  checked: PropTypes.bool.isRequired,
  active: PropTypes.bool.isRequired
}
