import React, { Component } from 'react'
import SelectField from 'shared/selectField'

import { calculateDates } from '../../rankings/utils'
import { calculateData, getEndMonth } from '../utils'
import API from 'api'
import NotificationsActions from 'actions/notifications'
import AppSignal from 'appsignal'

import Chart from './chart'
import accounting from 'accounting'

import { MONTHS, YEARS } from 'shared/catalogs'
import { PERIODS, TYPES_LABELS, PERIOD_LABELS } from '../../constants'
const FILL = {
  incomes: '#21c4bb',
  expenses: '#4184F5'
}

export default class HistoryBehavior extends Component {
  state = {
    data: [],
    total: 0,
    subtotal: 0,
    currentShow: 'total',
    filters: {
      period: 'trimester',
      month: new Date().getMonth(),
      year: YEARS[0].id // default current year
    }
  }

  componentDidMount () {
    this.loadData()
  }

  loadData = async () => {
    const { type } = this.props
    const { startMonth, endMonth } = calculateDates(this.state.filters)

    try {
      const { report } = await API.Reports.History({
        type,
        end_month: endMonth.format('YYYY-MM'),
        start_month: startMonth.format('YYYY-MM')
      })

      const { total, subtotal, data } = report.reduce(calculateData(type), {
        total: 0,
        subtotal: 0,
        data: []
      })

      this.setState({ data, total, subtotal })
      if (data.length > 0) {
        this.setState(state => ({
          filters: {
            ...state.filters,
            month: getEndMonth(data[data.length - 1].month)
          }
        }))
      }
    } catch (error) {
      AppSignal.sendError(new Error(error))
      NotificationsActions.error('No se ha podido cargar el historial')
    }
  }

  changeCurrentShow = e => {
    const { value } = e.target

    this.setState({ currentShow: value })
  }

  onChangeFilter = field => e => {
    const { value } = e.target

    this.setState(
      state => ({ filters: { ...state.filters, [field]: value } }),
      this.loadData
    )
  }

  render () {
    const { currentShow, filters, data, total, subtotal } = this.state
    const { type } = this.props

    return (
      <div>
        <div className='rubrosChart__filters rubrosChart__filters-wrap'>
          <div className='rubrosChart__filters rubrosChart__filters-filter'>
            <SelectField
              label='Periodo'
              options={PERIODS}
              value={filters.period}
              onChange={this.onChangeFilter('period')}
            />
          </div>
          <div className='rubrosChart__filters rubrosChart__filters-filter'>
            <SelectField
              label='Mes final'
              options={MONTHS}
              value={filters.month}
              onChange={this.onChangeFilter('month')}
            />
          </div>
          <div className='rubrosChart__filters rubrosChart__filters-filter'>
            <SelectField
              label='AÃ±o'
              options={YEARS}
              value={filters.year}
              onChange={this.onChangeFilter('year')}
            />
          </div>
        </div>
        <div className='columns'>
          <div className='column is-9'>
            <Chart
              data={data}
              show={currentShow}
              fill={FILL[type]}
              type={type}
            />
          </div>
          <div className='column is-3'>
            <div className='marginBottom'>
              <label className='rubrosChart--label' htmlFor='total'>
                <input
                  id='total'
                  type='radio'
                  value='total'
                  checked={currentShow === 'total'}
                  onChange={this.changeCurrentShow}
                />{' '}
                Total
              </label>
              <br />
              <label className='rubrosChart--label' htmlFor='subtotal'>
                <input
                  id='subtotal'
                  type='radio'
                  value='subtotal'
                  checked={currentShow === 'subtotal'}
                  onChange={this.changeCurrentShow}
                />{' '}
                Subtotal{' '}
                <span className='rubrosChart rubrosChart__table-title'>
                  (Total menos IVA)
                </span>
              </label>
            </div>
            <div className='marginBottom'>
              <p className='rubrosChart rubrosChart__table-title'>
                {TYPES_LABELS[type]} Total del {PERIOD_LABELS[filters.period]}:
              </p>
              <p className='rubrosChart rubrosChart__table-amount'>
                {accounting.formatMoney(total, '$ ', 0)}
              </p>
            </div>
            <div>
              <p className='rubrosChart rubrosChart__table-title'>
                {TYPES_LABELS[type]} Subtotal del{' '}
                {PERIOD_LABELS[filters.period]}:
              </p>
              <p className='rubrosChart rubrosChart__table-amount'>
                {accounting.formatMoney(subtotal, '$ ', 0)}
              </p>
            </div>
            {type === 'expenses' && (
              <div className='legend--container'>
                <span className='legend--text'>
                  <div className='legend--square legend--square__max' />
                  Mayor Gasto
                </span>
                <span className='legend--text'>
                  <div className='legend--square legend--square__min' />
                  Menor Gasto
                </span>
              </div>
            )}
          </div>
        </div>
      </div>
    )
  }
}
