import React, { Fragment } from 'react'
import { PREMIUM } from 'shared/plans/catalogsPlan'
import Tooltip from 'shared/tooltip'
import IconButton from 'shared/iconButton'
import classNames from 'classnames'
import Button from 'shared/button'
import { handleURL, getClientsPlan, formatCurrency } from 'utils'

const Premium = props => {
  const { premium, controlPlans, services } = props
  const title = !premium ? 'Tu plan va a cambiar a' : 'Tu plan actual es'
  const planName = getClientsPlan()
  const freePremium = planName === 'free_premium'
  let totalIVA = 0
  totalIVA =
    services &&
    services.reduce((accumulator, service) => {
      return Number(accumulator) + Number(service.amount)
    }, 0)
  return (
    <Fragment>
      {!premium && (
        <p className='premium__title-blue'>
          Quédate, continúa haciendo tu contabilidad sencilla
        </p>
      )}
      {premium ? (
        <div className='premium__row'>
          <div className='premium__row-left'>
            <p className='premium__actual-plan'>{title}</p>
            <p className='premium__title premium__title-in-row'>Premium</p>
            {freePremium && (
              <p className='premium__actual-plan'>Prueba de 30 días gratis</p>
            )}
          </div>
          <div className='premium__row-right'>
            <Button
              buttonStyle='primary'
              className='premium__row-button'
              onClick={controlPlans}
            >
              Mejorar Plan
            </Button>
            <Button onClick={() => handleURL('/cancelar-servicio')}>
              Cancelar plan
            </Button>
          </div>
        </div>
      ) : (
        <Fragment>
          <p className='premium__actual-plan'>{title}</p>
          <p className='premium__title'>Premium</p>
        </Fragment>
      )}
      <div className='premium__container'>
        {PREMIUM.map(({ specifications, price, periodicity }, index) => {
          return (
            <div key={index}>
              <p className='premium__price'>{`${formatCurrency(
                totalIVA
              )} MXN`}</p>
              <p className='premium__periodicity'>{periodicity}</p>
              {specifications.map(({ specification, tooltip }, index) => {
                return (
                  <div key={index} className='plans__container'>
                    <i className='mdi mdi-check plans__icon' />
                    <Tooltip tooltip={tooltip} className='plans__tooltip'>
                      <p
                        className={classNames({
                          plans__specification: tooltip
                        })}
                      >
                        {specification}
                        {tooltip && (
                          <IconButton
                            icon='comment-question'
                            className='plans__tooltip-icon'
                          />
                        )}
                      </p>
                    </Tooltip>
                  </div>
                )
              })}
            </div>
          )
        })}
      </div>
    </Fragment>
  )
}

export default Premium
