import BaseStore from './baseStore'

class DownloadsStore extends BaseStore {
  constructor () {
    super()
    this.filesList = []
    this.totalPages = 1
    this.totalItems = 0
    this.currentPage = 1

    this.actions = {
      DOWNLOADS_LIST_RECEIVED: action => {
        this.setFilesList(action.data)
      }
    }
  }

  // Returns stored total pages, this is used when explicitly you want to know it
  getTotalPages () {
    return this.totalPages
  }

  // Returns stored total pages, this is used when explicitly you want to know it
  getTotalItems () {
    return this.totalItems
  }

  // Returns stored current page, this is used when explicitly you want to know it
  getCurrentPage () {
    return this.currentPage
  }

  // Returns stored email list, this is used when explicitly you want to know it
  getFilesList () {
    return this.filesList
  }

  // Sets the email list from the data received from the api
  setFilesList (data) {
    this.filesList = this.camelCase(data.downloads)
    this.totalPages = data.pagination.total_pages
    this.totalItems = data.pagination.total_items
    this.currentPage = data.pagination.current_page
  }
}

export default DownloadsStore.getInstance()
