import BaseStore from './baseStore'

class Messages extends BaseStore {
  constructor () {
    super()
    this.messages = []
    this.pagination = {}

    this.actions = {
      SET_MESSAGES: action => {
        this.setMessages(action.data)
      },
      ADD_MESSAGE: action => {
        this.addMessage(action.data)
      }
    }
  }

  setMessages ({ messages, pagination }) {
    this.messages = messages.sort(
      (a, b) => new Date(b.created_at) - new Date(a.created_at)
    )
    this.pagination = pagination
  }

  addMessage (message) {
    this.messages = [message, ...this.messages]
  }

  getMessages () {
    return this.messages
  }

  getPagination () {
    return this.pagination
  }
}

export default Messages.getInstance()
