// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import moment from 'moment'
import accounting from 'accounting'
import classNames from 'classnames'
import { Async } from 'react-select'
import AppSignal from 'appsignal'

import API from 'api'
import PaymentAccountsActions from 'actions/paymentAccounts'
import NotificationsActions from 'actions/notifications'
import PaymentAccountsStore from 'stores/paymentAccounts'
import Modal from 'shared/modal'
import TextField from 'shared/textField'
import SelectField from 'shared/selectField'
import Datepicker from 'shared/datepicker'
import {
  absolutify,
  amountToCents,
  prettyDate,
  formatDateInvoice,
  DATE_FORMAT
} from 'utils'

import { PAYMENT_CATEGORIES, CURRENCY } from 'shared/catalogs'

const requiredFields = new Set()

function getCurrentState () {
  const paymentAccounts = PaymentAccountsStore.getPaymentAccounts()
  return {
    paymentAccounts: paymentAccounts.map(account => ({
      id: `${account.id}`,
      label: `${account.name} (${account.account_number})`
    }))
  }
}

class PaymentModal extends Component {
  constructor () {
    super()
    this.data = {
      account: '',
      description: '',
      exchange_rate: 1.0,
      date: moment(),
      amount: '0',
      includePaymentsComplement: false,
      currency: 'MXN',
      paymentForm: '',
      series: '',
      folio: '',
      branch: {
        value: ''
      }
    }
    this.state = Object.assign({}, this.data, getCurrentState())
    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    PaymentAccountsActions.getListWithCashAccounts()
    PaymentAccountsStore.addChangeListener(this._onChange)
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
    PaymentAccountsStore.removeChangeListener(this._onChange)
  }

  // Get clients for current taxable entity
  getBranches (input) {
    return API.EditClient.GetBranchesList({ description: input })
      .then(response => {
        return { options: this.parseBranches(response.branches) }
      })
      .catch(error => {
        AppSignal.sendError(error)
        throw error
      })
  }

  // Convert an array of branches in a format that react-select will understand
  parseBranches (branches) {
    return branches
      .map(branch => ({
        value: branch.id,
        label: branch.name
      }))
      .filter(branch => branch.label)
  }

  _onChange () {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    const state = getCurrentState()
    this.setState(state)
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validateRequiredFields () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate()) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  onChange (field, e) {
    this.setState({ [field]: e.target.value })
  }

  onChangeDate (field, e) {
    this.setState({ [field]: e })
  }

  togglePaymentsComplement () {
    requiredFields.clear()
    const { invoice } = this.props
    const amount = invoice.payments.reduce(
      (acc, payment) => acc - payment.amount_cents,
      invoice.total_cents
    )
    this.setState(prevState => ({
      includePaymentsComplement: !prevState.includePaymentsComplement,
      amount: accounting.formatMoney(absolutify(amount / 100), '$ ')
    }))
  }

  setBranch (branch) {
    this.setState({ branch })
  }

  // Format price as $ 1,234.56
  formatPrice (field) {
    const formatted = accounting.formatMoney(this.state[field], '$ ')
    this.setState({ [field]: formatted })
  }

  // Parse formatted price as '1234.56'
  unformatPrice (field) {
    const unformatted = absolutify(this.state[field]).toString()
    this.setState({ [field]: unformatted })
  }

  // Sends payment hash to the API
  addPayment () {
    if (this.validateRequiredFields()) {
      const { paymentId, invoice, toggleModal, getInvoice } = this.props
      const {
        date,
        description,
        amount,
        account,
        includePaymentsComplement,
        paymentForm,
        currency,
        exchangeRate,
        branch,
        series,
        folio
      } = this.state
      const amountCents = absolutify(amountToCents(amount).toFixed(0)) // Rounds to integer
      const restingAmount = absolutify(
        invoice.payments.reduce(
          (acc, payment) => acc - payment.amount_cents,
          invoice.total_cents
        )
      )

      if (amountCents > restingAmount && invoice.currency === 'MXN') {
        return NotificationsActions.error(
          'La suma de las parcialidades no debe exceder el total a pagar.'
        )
      }
      let data = {
        payment: {
          paid_on: moment(date).format(DATE_FORMAT),
          description,
          amount_cents: amountCents,
          payment_account_id: Number(account)
        }
      }
      if (includePaymentsComplement) {
        data = Object.assign({}, data, {
          invoice: {
            issue_date: invoice.issue_date,
            voucher_type: invoice.voucher_type,
            series,
            folio,
            payment_detail_mg: {
              payment_mgs: [
                {
                  exchange_rate: exchangeRate,
                  paid_at: formatDateInvoice(date),
                  payment_form: paymentForm,
                  currency,
                  amount_cents: amountCents
                }
              ]
            }
          }
        })
        if (branch && branch.value) {
          data = Object.assign({}, data, {
            branch: { id: branch.value }
          })
        }
      }
      toggleModal()
      if (paymentId) {
        API.Payments.Update(paymentId, data)
          .then(r => {
            getInvoice()
            NotificationsActions.success(
              'El pago fue actualizado correctamente'
            )
          })
          .catch(error => {
            AppSignal.sendError(error)
            NotificationsActions.error(error)
          })
      } else {
        API.Payments.Create(invoice.id, data)
          .then(r => {
            getInvoice()
            NotificationsActions.success('El pago fue creado correctamente')
          })
          .catch(error => {
            AppSignal.sendError(error)
            NotificationsActions.error(error)
          })
      }
    }
  }

  // Render payment invoice check if invoice is not 3.3
  renderAddPaymentInvoice () {
    const { invoice } = this.props
    const { includePaymentsComplement } = this.state
    if (invoice.version !== '3.3') return null
    if (
      invoice['payment_method'] !== 'PPD' &&
      invoice['payment_form'] !== '99'
    ) {
      return null
    }
    return (
      <div className='column is-12'>
        <label className='checkbox'>
          <input
            onClick={this.togglePaymentsComplement.bind(this)}
            type='checkbox'
            checked={includePaymentsComplement}
          />
          &nbsp;AÃ±adir complemento de pago de factura
        </label>
      </div>
    )
  }

  // Render payments complement fields if complement is enabled
  renderPaymentsComplementFields () {
    const {
      includePaymentsComplement,
      paymentForm,
      currency,
      branch,
      series,
      folio
    } = this.state
    if (!includePaymentsComplement) return null
    return (
      <div>
        <div className='column is-12'>
          <TextField
            label='Serie'
            onChange={this.onChange.bind(this, 'series')}
            value={series}
            selectAllOnFocus
            required
            ref={r => {
              if (includePaymentsComplement) requiredFields.add(r)
            }}
          />
        </div>
        <div className='column is-12'>
          <TextField
            label='Folio'
            onChange={this.onChange.bind(this, 'folio')}
            value={folio}
            selectAllOnFocus
            required
            ref={r => {
              if (includePaymentsComplement) requiredFields.add(r)
            }}
          />
        </div>
        <div className='column is-12'>
          <SelectField
            label='Moneda'
            placeholder='Seleccione una moneda'
            options={CURRENCY}
            onChange={this.onChange.bind(this, 'currency')}
            value={currency}
            required
            ref={r => {
              if (includePaymentsComplement) requiredFields.add(r)
            }}
            showId
          />
        </div>
        <div className='column is-12'>
          <SelectField
            label='Forma de pago'
            placeholder='Seleccione una forma de pago'
            options={PAYMENT_CATEGORIES}
            onChange={this.onChange.bind(this, 'paymentForm')}
            value={paymentForm}
            required
            ref={r => {
              if (includePaymentsComplement) requiredFields.add(r)
            }}
            showId
          />
        </div>
        <div className='column is-12'>
          <label className='caption'>Sucursal de emisiÃ³n (opcional)</label>
          <div className='control has-info'>
            <Async
              value={branch}
              loadOptions={this.getBranches.bind(this)}
              clearable
              placeholder='Selecciona una Sucursal'
              loadingPlaceholder='Buscando'
              searchPromptText='Teclear el nombre de la sucursal'
              noResultsText='No encontrada'
              onChange={this.setBranch.bind(this)}
              ignoreAccents={false}
            />
            <span className='icon info-icon'>
              <span className='tooltip'>
                Para agregar mÃ¡s sucursales ingresa a "mi empresa"
              </span>
              <i className='mdi mdi-information-outline' />
            </span>
          </div>
        </div>
      </div>
    )
  }

  // Main render
  render () {
    const { isActive, toggleModal, invoice } = this.props
    const {
      account,
      paymentAccounts,
      description,
      exchangeRate,
      date,
      amount
    } = this.state
    const payedCents = invoice.payments
      ? invoice.payments.reduce((acc, payment) => acc + payment.amount_cents, 0)
      : 0
    const restingCents = invoice.total_cents - payedCents
    return (
      <Modal
        title={`Registrar ${invoice.type === 0 ? 'cobro' : 'pago'}`}
        isActive={isActive}
        toggleModal={toggleModal}
        onSubmit={this.addPayment.bind(this)}
      >
        <form onSubmit={this.addPayment.bind(this)}>
          <div className='columns is-multiline'>
            <div className='column is-12 flex-column'>
              <span className='subtitle-body'>
                Emisor: {invoice.issuer ? invoice.issuer.name : ''}
              </span>
              <span className='subtitle-body'>
                Receptor: {invoice.recipient ? invoice.recipient.name : ''}
              </span>
              <span className='body'>Folio: {invoice.folio}</span>
              <span className='body'>
                Fecha de emisiÃ³n: {prettyDate(invoice.issue_date)}
              </span>
              <span className='body'>
                Total: {accounting.formatMoney(invoice.total_cents / 100, '$ ')}
              </span>
              <span
                className={classNames('body', {
                  'is-hidden': invoice.payment_status !== '1'
                })}
              >
                Pagado: {accounting.formatMoney(payedCents / 100, '$ ')}
              </span>
              <span
                className={classNames('body', {
                  'is-hidden': invoice.payment_status !== '1'
                })}
              >
                Restante: {accounting.formatMoney(restingCents / 100, '$ ')}
              </span>
            </div>
            {this.renderAddPaymentInvoice()}
            <div className='column is-12'>
              <SelectField
                label='Cuenta de depÃ³sito'
                placeholder='Seleccione una cuenta de pago'
                options={paymentAccounts}
                onChange={this.onChange.bind(this, 'account')}
                value={account}
                required
                ref={r => requiredFields.add(r)}
              />
            </div>
            <div className='column is-12'>
              <TextField
                label='DescripciÃ³n'
                onChange={this.onChange.bind(this, 'description')}
                value={description}
                required
                ref={r => requiredFields.add(r)}
              />
            </div>
            <div className='column is-12'>
              <Datepicker
                label={`Fecha de ${invoice.type === 0 ? 'cobro' : 'pago'}`}
                value={date}
                onChange={this.onChangeDate.bind(this, 'date')}
                required
                ref={r => requiredFields.add(r)}
                maxDate={moment()}
              />
            </div>
            <div className='column is-8'>
              <TextField
                label='Importe'
                onChange={this.onChange.bind(this, 'amount')}
                onBlur={this.formatPrice.bind(this, 'amount')}
                onFocus={this.unformatPrice.bind(this, 'amount')}
                value={amount}
                selectAllOnFocus
                required
                ref={r => requiredFields.add(r)}
              />
            </div>
            {invoice.currency !== 'MXN' && (
              <div className='column is-4'>
                <TextField
                  label='Tipo de cambio'
                  onChange={this.onChange.bind(this, 'exchangeRate')}
                  value={exchangeRate}
                  selectAllOnFocus
                  ref={r => requiredFields.add(r)}
                />
              </div>
            )}
            {this.renderPaymentsComplementFields()}
          </div>
        </form>
      </Modal>
    )
  }
}

export default PaymentModal

PaymentModal.propTypes = {
  invoice: PropTypes.object.isRequired,
  isActive: PropTypes.bool,
  toggleModal: PropTypes.func,
  getInvoice: PropTypes.func,
  paymentId: PropTypes.string
}

PaymentModal.defaultProps = {
  isActive: false,
  toggleModal: () => {},
  getInvoice: () => {}
}
