import React, { Component } from 'react'
import PropTypes from 'prop-types'

const CaptionTitle = ({ children }) => (
  <div className='caption'> {children}</div>
)

CaptionTitle.propTypes = {
  children: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.element,
    PropTypes.object,
    PropTypes.array
  ])
}

const CaptionStatus = ({ children }) => (
  <div className='caption flex-center'>{children}</div>
)

CaptionStatus.propTypes = {
  children: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.element,
    PropTypes.arrayOf(PropTypes.element)
  ])
}

const CaptionIcon = ({ children, icon, className = '', status }) => (
  <span style={{ marginLeft: '1rem' }} className='flex-center'>
    <i
      className={`mdi mdi-${icon} ${status} ${className}`}
      style={{ fontSize: 'small' }}
    />
    {children}
  </span>
)

CaptionIcon.propTypes = {
  children: PropTypes.oneOfType([PropTypes.string, PropTypes.element]),
  icon: PropTypes.string,
  className: PropTypes.string,
  status: PropTypes.oneOf(['success', 'danger', 'warning', 'info'])
}

export default class Caption extends Component {
  static CaptionTitle = CaptionTitle
  static CaptionStatus = CaptionStatus
  static CaptionIcon = CaptionIcon
  render () {
    return (
      <div className='flex-between' style={{ paddingBottom: '1rem' }}>
        {this.props.children}
      </div>
    )
  }
}

Caption.propTypes = {
  children: PropTypes.oneOfType([PropTypes.array, PropTypes.element])
}
