// Libraries
import React from 'react'
import update from 'immutability-helper'
import uuid from 'uuid'

// Components
import Button from 'shared/button'

// Children
import SpecificDescription from './specificDescription'

const SPECIFIC_DESCRIPTION_KEY = 'specificDescription'

class SpecificDescriptions extends React.Component {
  constructor () {
    super()
    this.data = {
      [SPECIFIC_DESCRIPTION_KEY]: []
    }
    this.state = { ...this.data }
  }

  componentDidMount () {
    this.addSpecificDescription()
  }

  validate () {
    return this.state[SPECIFIC_DESCRIPTION_KEY].map(specificDescription =>
      this.refs[specificDescription.uuid].validate()
    ).every(e => e)
  }

  // Exports specificDescriptions object
  export () {
    return {
      ...this.state[SPECIFIC_DESCRIPTION_KEY].map(({ uuid }) =>
        this.refs[uuid].export()
      )
    }
  }

  // Create a new specificDescription and append it to state
  addSpecificDescription () {
    const specificDescription = this.state[SPECIFIC_DESCRIPTION_KEY]
    this.setState({
      [SPECIFIC_DESCRIPTION_KEY]: update(specificDescription, {
        [specificDescription.length]: { $set: { uuid: uuid() } }
      })
    })
  }

  // Return a new array with current addresses except the one to be deleted
  deleteSpecificDescription (uuid) {
    this.setState({
      [SPECIFIC_DESCRIPTION_KEY]: this.state[SPECIFIC_DESCRIPTION_KEY].filter(
        c => c.uuid !== uuid
      )
    })
  }

  // Renders Address components
  renderSpecificDescription () {
    return this.state[SPECIFIC_DESCRIPTION_KEY].length > 0
      ? this.state[SPECIFIC_DESCRIPTION_KEY].map(({ uuid }) => (
        <SpecificDescription
          key={uuid}
          ref={uuid}
          uuid={uuid}
          deletable={this.state[SPECIFIC_DESCRIPTION_KEY].length > 1}
          deleteSpecificDescription={this.deleteSpecificDescription.bind(
            this
          )}
        />
      ))
      : null
  }

  // Mains render
  render () {
    return (
      <div>
        {this.renderSpecificDescription()}
        <Button
          buttonStyle='primaryLink'
          icon='plus'
          onClick={this.addSpecificDescription.bind(this)}
        >
          Agregar Descripción
        </Button>
      </div>
    )
  }
}

export default SpecificDescriptions
