import React, { PureComponent, Fragment } from 'react'
import { getPlanName, handleModals, getClientsPlan } from 'utils'
import Button from 'shared/button'
import classNames from 'classnames'
import { modalPlans } from '../../../app/views/viewInvoice/utilsRestrictions'

export default class ButtonPlan extends PureComponent {
  state = { showBannerModal: false }

  render () {
    const planName = getPlanName()
    const plan = getClientsPlan()

    return (
      <Fragment>
        {plan === 'integral_service' ? (
          <p className='encontaButton-plan encontaButton-plan--integral-service'>
            <i className='mdi mdi-star' />
            {planName}
          </p>
        ) : (
          <Button
            buttonStyle={classNames('encontaButton-plan', {
              'encontaButton-plan--premium':
                plan === 'premium' || plan === 'free_premium',
              'encontaButton-plan--free': plan === 'free'
            })}
            onClick={() => handleModals('showBannerModal', this)}
            icon={'star'}
          >
            {planName}
          </Button>
        )}
        {modalPlans(this)}
      </Fragment>
    )
  }
}
