import React from 'react'
import PropTypes from 'prop-types'

import CiecCard from './CiecCard'
import FielCard from './FielCard'

const CredentialsCard = props => {
  const { isShowedCiec, isShowedFiel } = props
  return (
    <div className='credentials'>
      {isShowedCiec && isShowedFiel && (
        <p className='credentials__information'>
          Te recomendamos ingresar alguna de las siguientes opciones para
          iniciar la sincronización de tus facturas con el SAT, y así conocer
          toda la experiencia de Enconta.
        </p>
      )}
      <div className='credentials__container'>
        {isShowedCiec && (
          <CiecCard key='ciec_card' isShowedFiel={isShowedFiel} />
        )}
        {isShowedCiec && isShowedFiel && (
          <div className='credentials__vertical-line' />
        )}
        {isShowedFiel && (
          <FielCard key='fiel_card' isShowedCiec={isShowedCiec} />
        )}
      </div>
    </div>
  )
}

export default CredentialsCard

CredentialsCard.propTypes = {
  isShowedCiec: PropTypes.bool,
  isShowedFiel: PropTypes.bool
}

CredentialsCard.defaultProps = {
  isShowedCiec: true,
  isShowedFiel: true
}
