// Libraries
import React, { Component, Fragment } from 'react'
import { Link } from 'react-router'
import { formatCurrency, prettyDate } from 'utils'
import classNames from 'classnames'

// Action
import SuscriptionActions from 'actions/suscription'
import NotificationsActions from 'actions/notifications'

// Stores
import SuscriptionStore from 'stores/suscription'

// Components
import Paginator from 'shared/paginator'

// Catalogs
import { PAYMENTLOG_STATUS, MONTHS } from 'shared/catalogs'

function getCurrentState () {
  return {
    paymentLog: SuscriptionStore.getPaymentsLog(),
    totalPages: SuscriptionStore.getTotalPages(),
    totalItems: SuscriptionStore.getTotalItems()
  }
}

export default class PaymentHistory extends Component {
  state = {
    ...getCurrentState()
  }
  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    SuscriptionStore.addChangeListener(this._onChange)
    SuscriptionActions.getPaymentLogs().catch(error => {
      NotificationsActions.error(error)
    })
  }

  _onChange = () => {
    const suscriptionStructure = getCurrentState()
    this.setState(suscriptionStructure)
  }

  // Removes the listener to avoid memory leaks
  componentWillUnmount () {
    SuscriptionStore.removeChangeListener(this._onChange)
  }

  renderRows () {
    const { paymentLog } = this.state
    let paymentHistoryRow = (
      <tr className='block unselectable'>
        <td className='has-text-centered' colSpan={6}>
          No hay servicios
        </td>
      </tr>
    )

    if (paymentLog.length > 0) {
      paymentHistoryRow = paymentLog.map(movement => {
        const [monthId, year] = movement.period.split('-')
        const rowMonths = MONTHS.find(month => month.id === monthId) || {
          label: ''
        }

        let { label } = PAYMENTLOG_STATUS.find(
          status => status.id === movement.status
        ) || { label: 'Pendiente' }

        return (
          <tr>
            <td>
              {prettyDate(movement.payment_date, null, false, false, ' ')}
            </td>
            <td>{movement.description}</td>
            <td>{`${rowMonths.label} ${year}`}</td>
            <td>{formatCurrency(movement.total)}</td>
            <td
              className={classNames({
                danger: movement.status === 'declined',
                success: movement.status === 'paid'
              })}
            >
              {label}
            </td>
            <td>
              {movement.status === 'invoiced' && (
                <Link
                  to={`/facturas/ver/${movement.invoice_id}`}
                  className='is-uppercase link--underline'
                  download
                >
                  <i className='mdi mdi-file-outline icon-aligned icon-aligned--margin-right' />
                  Ver
                </Link>
              )}
            </td>
          </tr>
        )
      })
    }
    return paymentHistoryRow
  }

  renderTable () {
    const { totalPages } = { ...this.state }
    return (
      <Fragment>
        <table className='table history-payments'>
          <thead>
            <th>Fecha de cobro</th>
            <th>Descripción</th>
            <th>Periodo cubierto</th>
            <th className='padding-bottom-less'>
              Total
              <br />
              <p className='note'>IVA incluido</p>
            </th>
            <th>Estatus</th>
            <th>Descarga</th>
          </thead>
          <tbody>{this.renderRows()}</tbody>
        </table>
        <Paginator totalPages={totalPages} location={this.props.location} />
      </Fragment>
    )
  }

  render () {
    return (
      <div>
        <Link
          to='/mi-cuenta/suscripcion'
          className='is-uppercase link--underline link-previous'
        >
          <i className='mdi mdi-chevron-double-left icon-aligned icon-aligned--margin-left' />
          Volver
        </Link>
        <p className='subheader-green'>Historial de Pagos</p>
        <p className='paragraph-info paragraph-info--font-12px'>
          Las cuotas serán facturadas 3 días después de que el cobro haya sido
          aprobado.
        </p>
        {this.renderTable()}
      </div>
    )
  }
}
