// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { browserHistory, Link } from 'react-router'
import update from 'immutability-helper'

import Button from 'shared/button'

// Catalogs
import { SAT_CATEGORIES } from 'shared/catalogs'

// Constants
const CATEGORY_CATALOG_START = 1
const CATEGORY_ID_POSITION = 0
const CATEGORY_LEVEL_POSITION = 1
const CATEGORY_GROUPING_CODE_POSITION = 2
const CATEGORY_NAME_POSITION = 3
const CATEGORY_ANCESTRY_POSITION = 4

const CATEGORY_LABEL_ID = 'id'
const CATEGORY_LABEL_LEVEL = 'level'
const CATEGORY_LABEL_GROUPING_CODE = 'grouping_code'
const CATEGORY_LABEL_NAME = 'name'
const CATEGORY_LABEL_ANCESTRY = 'ancestry'

class TaxableEntities extends Component {
  constructor (props) {
    super(props)
    this.data = {
      entityId: this.props.params.entityId,
      categories: []
    }
    this.state = Object.assign({}, this.data)
  }

  componentDidMount () {
    this.getCategories()
  }

  getCategories () {
    let categories = []
    for (let i = CATEGORY_CATALOG_START; i < SAT_CATEGORIES.length; i++) {
      categories.push({
        selected: false,
        [CATEGORY_LABEL_ID]: SAT_CATEGORIES[i][CATEGORY_ID_POSITION],
        [CATEGORY_LABEL_LEVEL]: SAT_CATEGORIES[i][CATEGORY_LEVEL_POSITION],
        [CATEGORY_LABEL_GROUPING_CODE]:
          SAT_CATEGORIES[i][CATEGORY_GROUPING_CODE_POSITION],
        [CATEGORY_LABEL_NAME]: SAT_CATEGORIES[i][CATEGORY_NAME_POSITION],
        [CATEGORY_LABEL_ANCESTRY]:
          SAT_CATEGORIES[i][CATEGORY_ANCESTRY_POSITION] || ''
      })
    }
    this.setState({ categories: categories })
  }

  renderNewButton (right) {
    return (
      <div className='field is-grouped'>
        <span className='control'>
          <Button onClick={this.sendData.bind(this)}>Guardar</Button>
        </span>
        <span className='control'>
          <Link to={'/admin/entidades_fiscales/'}>
            <Button>Cancelar</Button>
          </Link>
        </span>
      </div>
    )
  }

  sendData () {
    // Get selected categories
    let selectedCategories = []
    for (let i = 0; i < this.state.categories.length; i++) {
      if (this.state.categories[i].selected) {
        selectedCategories.push(this.state.categories[i])
      }
    }

    // Log'em
    console.log(selectedCategories)

    // Return
    browserHistory.push('/admin/entidades_fiscales')
  }

  toggleCategory (catId) {
    let categoryPosition = 0
    // Get TRUE position of category, currently maps to 1:1, 2:2, 3:3, ..., 123:123 (zero index)
    for (
      let i = CATEGORY_CATALOG_START;
      i < this.state.categories.length;
      i++
    ) {
      if (catId === this.state.categories[i][CATEGORY_LABEL_ID]) {
        categoryPosition = i
      }
    }
    this.setState({
      categories: update(this.state.categories, {
        [categoryPosition]: {
          selected: { $set: !this.state.categories[categoryPosition].selected }
        }
      })
    })
  }

  renderCategories () {
    let renderCategories = (
      <article className='message'>
        <div className='message-body'>No hay categor√≠as.</div>
      </article>
    )
    if (this.state.categories.length > 0) {
      renderCategories = this.state.categories.map(
        function (category, index) {
          return (
            <div key={index}>
              <div className='field'>
                <p className='control'>
                  <label className='checkbox' htmlFor='categories'>
                    <input
                      id='categories'
                      type='checkbox'
                      onChange={this.toggleCategory.bind(
                        this,
                        category[CATEGORY_LABEL_ID]
                      )}
                    />
                    {category[CATEGORY_LABEL_GROUPING_CODE] +
                      ' - ' +
                      category[CATEGORY_LABEL_NAME]}
                  </label>
                </p>
              </div>
            </div>
          )
        }.bind(this)
      )
    }
    return renderCategories
  }

  render () {
    return (
      <div>
        <h1 className='title is-4'>Asociar Rubros</h1>
        {this.renderCategories()}
        {this.renderNewButton()}
      </div>
    )
  }
}

TaxableEntities.propTypes = {
  params: PropTypes.shape({
    entityId: PropTypes.string
  })
}

export default TaxableEntities
