import React, { Component } from 'react'
import { Link } from 'react-router'
import classNames from 'classnames'

import SessionActions from 'actions/session'
import Logo from '../logo'

class PublicMenu extends Component {
  constructor (props) {
    super(props)
    this.data = {
      logoSize: 'normal',
      hasSession: false
    }
    this.state = Object.assign({}, this.data)
    this.changeLogoSize = this.changeLogoSize.bind(this)
  }

  componentDidMount () {
    window.addEventListener('optimizedResize', this.changeLogoSize)
    this.changeLogoSize()
    this.setState({ hasSession: SessionActions.hasSession() })
  }

  componentWillUnmount () {
    window.removeEventListener('optimizedResize', this.changeLogoSize)
  }

  changeLogoSize () {
    this.setState({
      logoSize: window.innerWidth >= 1200 ? 'normal' : 'small'
    })
  }

  render () {
    const { logoSize, hasSession } = this.state
    return (
      <div className='topbar'>
        <div className='topbar__brand'>
          <Logo textColour={'white'} logoSize={logoSize} />
        </div>
        <div
          className={classNames('topbar__back-to-app', {
            'topbar__back-to-app--no-session': !hasSession
          })}
        >
          {hasSession && (
            <Link className='topbar__back-to-app__link' to='/'>
              <i className='mdi mdi-chevron-double-left' />
              <p>Volver a la Aplicación</p>
            </Link>
          )}

          {!hasSession && (
            <Link
              to='/ingresar'
              className='topbar__submenu-button topbar__submenu-button--primary'
            >
              <i className='mdi mdi-login topbar__icon' />
              Iniciar Sesión
            </Link>
          )}
        </div>
      </div>
    )
  }
}

export default PublicMenu
