// Libraries
import React, { Component } from 'react'
import { Link } from 'react-router'

import SatCredentialsActions from 'actions/satCredentials'

import SatCredentialsStore from 'stores/satCredentials'

const getCurrentState = () => {
  return {
    credentials: SatCredentialsStore.getCredentialStatus()
  }
}
class Footer extends Component {
  constructor (props) {
    super(props)
    this.state = { ...getCurrentState() }
  }

  componentDidMount () {
    SatCredentialsStore.addChangeListener(this._onChange)
    this.loadData()
  }

  componentWillUnmount () {
    SatCredentialsStore.removeChangeListener(this._onChange)
  }

  _onChange = () => {
    const state = getCurrentState()
    this.setState(state)
  }

  loadData () {
    SatCredentialsActions.getCredentialsStatus()
  }

  render () {
    const { client } = this.state.credentials
    return (
      <footer className='footer'>
        <div className='footer__item'>
          <Link to='/solicitud-de-credito' className='caption highlighted'>
            Solicitud de Crédito
          </Link>
        </div>
        {client && (
          <div className='footer__item'>
            <Link to='/solicitar-baja' className='caption'>
              Solicitar Baja
            </Link>
          </div>
        )}
        <div className='footer__item'>
          <Link to='/acerca-de' className='caption'>
            Información Legal
          </Link>
        </div>
        <div className='footer__item'>
          <Link to='/registro-de-cambios' className='caption'>
            Registro de Cambios
          </Link>
        </div>
        <div className='footer__item'>
          <Link to='/seguridad' className='caption'>
            Avisos Datos Personales
          </Link>
        </div>
      </footer>
    )
  }
}

export default Footer
