// Libraries
import React from 'react'

// Components
import TextField from 'shared/textField'

// Children
import Address from './../address'

const CURP_KEY = 'curp'

const requiredFields = new Set()

class Issuer extends React.Component {
  constructor (props) {
    super(props)
    this.data = {
      [CURP_KEY]: ''
    }
    this.state = Object.assign({}, this.data)
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validate () {
    const requiredIsValid = [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate()) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded

    const addressIsValid = this.refs['address'].validate()

    return requiredIsValid && addressIsValid
  }

  export () {
    return Object.assign({}, this.state, {
      address: this.refs['address'].export()
    })
  }

  // Updates an input's value when it changes
  onChange (field, e) {
    this.setState({
      [field]: e.target.value
    })
  }

  // Main render
  render () {
    return (
      <div>
        <p className='content'>
          <span className='title'>Emisor</span>
        </p>
        <TextField // curp, 18 caracteres personas morales no registran curp
          label='CURP'
          placeholder='CURP'
          value={this.state[CURP_KEY]}
          onChange={this.onChange.bind(this, CURP_KEY)}
        />
        <br />
        <Address ref='address' />
      </div>
    )
  }
}

export default Issuer
