import React, { Component } from 'react'
import DocumentTitle from 'react-document-title'
import NotificationsContainer from 'shared/notifications'
import Button from 'shared/button'
import ViewDoc from 'shared/viewDoc'
import { handleBack } from 'utils'

import DiagnosisStore from 'stores/diagnosis'
import SessionStore from 'stores/session'

import RegisterActions from 'actions/register'

const getCurrentState = () => {
  return {
    url: DiagnosisStore.getUrl()
  }
}
class ShowDiagnostic extends Component {
  state = { ...getCurrentState() }

  componentDidMount () {
    DiagnosisStore.addChangeListener(this._onChange)
    const currentEntity = SessionStore.getCurrentEntity()
    RegisterActions.askDiagnosisStatus(currentEntity.id)
  }

  componentWillUnmount () {
    DiagnosisStore.removeChangeListener(this._onChange)
  }

  _onChange = () => {
    this.setState(getCurrentState())
  }

  render () {
    const { url } = this.state
    return (
      <DocumentTitle title={'Diagnóstico en proceso - Enconta'}>
        <div className='register'>
          <Button
            buttonStyle='link'
            icon='chevron-double-left'
            onClick={handleBack}
            className='has-text-left is-fullwidth'
          >
            Volver
          </Button>
          <ViewDoc src={url} />
          <div className='notification-container notification-centered'>
            <NotificationsContainer />
          </div>
        </div>
      </DocumentTitle>
    )
  }
}

export default ShowDiagnostic
