import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router'
import classNames from 'classnames'
import ReactGa from 'react-ga'
import { createGaEvent, getClientsPlan } from 'utils'

import Button from 'shared/button'
import ButtonPlan from 'shared/buttonPlan'

import SessionActions from 'actions/session'

import SessionStore from 'stores/session'

// Items that will populate the sidebar
const MENU_ITEMS = [
  {
    title: 'Inicio',
    route: '/dashboard',
    icon: 'mdi mdi-home-outline',
    admin: false,
    feature: 'default',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  },
  {
    title: 'Ingresos',
    route: '/ingresos',
    icon: 'mdi mdi-import',
    admin: false,
    feature: 'default',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  },
  {
    title: 'Gastos',
    route: '/gastos',
    icon: 'mdi mdi-export',
    admin: false,
    feature: 'default',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  },
  {
    title: 'Mi Contabilidad',
    route: '/mi-contabilidad',
    icon: 'mdi mdi-file-table-outline',
    admin: false,
    feature: 'default',
    availableFor: ['integral_service']
  },
  /* TODO: uncomment when finished */
  // {
  //   title: 'Inventarios',
  //   route: '/inventarios',
  //   icon: 'mdi mdi-inbox-multiple',
  //   admin: false,
  //   feature: 'inventory'
  // },
  {
    title: 'Reportes',
    route: '/reportes',
    icon: 'mdi mdi-chart-bar',
    admin: false,
    feature: 'default',
    availableFor: ['free_premium', 'premium', 'integral_service']
  },
  {
    title: 'Documentos',
    route: '/documentos',
    icon: 'mdi mdi-file-outline',
    admin: false,
    feature: 'default',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  },
  {
    title: 'Facturas Enviadas',
    route: '/emails',
    icon: 'mdi mdi-email-check-outline',
    admin: false,
    feature: 'default',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  },
  {
    title: 'Descargas',
    route: '/descargas',
    icon: 'mdi mdi-arrow-down-bold-circle-outline',
    admin: false,
    feature: 'default',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  },
  {
    title: 'Entidades Fiscales',
    route: '/admin/entidades_fiscales?status=client&platform_status=active',
    icon: 'mdi mdi-account-switch',
    admin: true,
    feature: 'default',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  }
]

const MOBILE_MENU_ITEMS = [
  {
    route: '/mi-cuenta/general',
    icon: 'mdi mdi-account-edit',
    title: 'Mi Cuenta',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  },
  {
    route: '/entidades',
    icon: 'mdi mdi-briefcase-account',
    title: 'Mi Empresa',
    availableFor: ['free_premium', 'free', 'premium', 'integral_service']
  }
]

const getCurrentState = () => ({
  features: SessionStore.getFeatures()
})

/**
 * This component will appear as a sidebar on desktop size, for small sizes it
 * is going to be hidden. It is handled by the small logo to show it.
 */
class Sidebar extends PureComponent {
  state = { features: [], ...getCurrentState() }

  componentDidMount () {
    SessionStore.addChangeListener(this._onChange)
    document.addEventListener('mousedown', this.handleClickOutside)
    this.loadData()
  }

  componentWillUnmount = () => {
    SessionStore.removeChangeListener(this._onChange)
    document.removeEventListener('mousedown', this.handleClickOutside)
  }

  _onChange = () => {
    this.setState(getCurrentState())
  }

  loadData = () => {
    SessionActions.setFeatures()
  }

  handleLinks = ({ title }) => {
    ReactGa.event(createGaEvent('inicio', 'click on button', title))
    if (window.innerWidth < 1200) {
      this.props.toggleMenu()
    }
  }

  handleClickOutside = event => {
    const { className: parentClass, tagName } = event.target.parentNode
    const isLogo =
      tagName === 'DIV' ? parentClass.includes('enconta-logo ') : true

    if (this.wrapperRef && !this.wrapperRef.contains(event.target) && !isLogo) {
      this.props.toggleMenu()
    }
  }

  setWrapperRef = node => {
    this.wrapperRef = node
  }

  /**
   * Builds dynamically the client's menu, if the feature exists it will be shown
   * otherwise adds the default features if doesn't admin only
   * @param {array} features
   */
  setUpUserMenu = features => {
    return MENU_ITEMS.filter(item => {
      const { role } = SessionStore.getUser()
      const element = features.find(feature => feature.key === item.feature)

      if (element) {
        return element.enabled && item
      }

      if (role === 'account_holder') {
        return item.feature === 'default' && !item.admin && item
      } else {
        return item.feature === 'default' && item
      }
    })
  }

  renderMenuItem = (item, index) => {
    const { pathname } = this.props
    const menuOptions = pathname.split('/').filter(option => option !== '')
    const baseRouteOption = `/${menuOptions[0]}`
    const plan = getClientsPlan()
    const disabled = !item.availableFor.includes(plan)
    return (
      <li
        className={classNames('main-menu__option', {
          'main-menu__option--active': baseRouteOption.includes(item.route),
          'main-menu__option--disabled': disabled
        })}
        key={index}
      >
        {disabled ? (
          <div className='main-menu__link main-menu__link--disabled'>
            <i className={`${item.icon}`} />
            <p className='main-menu__title'>{item.title}</p>
          </div>
        ) : (
          <Link
            to={item.route}
            onClick={() => {
              this.handleLinks(item)
            }}
            className='main-menu__link'
          >
            <i className={`${item.icon}`} />
            <p className='main-menu__title'>{item.title}</p>
          </Link>
        )}
      </li>
    )
  }

  getLogOutButton = () => {
    return (
      <Button
        buttonStyle='encontaButton--main-menu-button'
        icon='logout'
        onClick={SessionActions.logout}
      >
        Cerrar Sesi√≥n
      </Button>
    )
  }

  addMobileItemsMenu = menu => {
    const mobileOptions = MOBILE_MENU_ITEMS.map((item, index) =>
      this.renderMenuItem(item, index)
    )
    const logout = this.getLogOutButton()

    menu = [...menu, ...mobileOptions]
    menu.push(logout)

    return menu
  }

  /**
   * Builds the main menu according to features activated and screen size
   * @param  {Array} features
   */
  buildMenu = features => {
    const enabledMenuItems = this.setUpUserMenu(features)
    let buildedMenu = enabledMenuItems.map((item, index) =>
      this.renderMenuItem(item, index)
    )

    if (window.innerWidth < 768) {
      buildedMenu = this.addMobileItemsMenu(buildedMenu)
    }

    return buildedMenu
  }

  render () {
    const { features } = this.state
    return (
      <nav className='main-menu' ref={this.setWrapperRef}>
        <ButtonPlan />
        <ul className='main-menu__container'>{this.buildMenu(features)}</ul>
      </nav>
    )
  }
}

Sidebar.propTypes = {
  pathname: PropTypes.string.isRequired,
  toggleMenu: PropTypes.func.isRequired
}

Sidebar.defaultProps = {
  pathname: '/',
  toggleMenu: () => {}
}

export default Sidebar
