import BaseStore from './baseStore'

class SatCredentialsStore extends BaseStore {
  constructor () {
    super()
    this.credentials = {
      fiel: {
        password: '',
        key_file: '',
        cer_file: ''
      },
      csd: {
        password: '',
        key_file: '',
        cer_file: ''
      },
      ciec: '',
      status: {
        has_fiel: true,
        has_csd: true,
        has_ciec: true
      }
    }

    this.actions = {
      SAT_CREDENTIALS_RECEIVED: action => {
        this.setCredentials(action.data)
      },
      SAT_CREDENTIALS_UPDATED: action => {
        console.log(action.data)
      },
      SAT_CREDENTIALS_RECEIVED_STATUS: action => {
        this.setCredentialStatus(action.data)
      }
    }
  }

  // Returns stored loader percent, this is used when explicitly you want to know it
  getCredentials () {
    return this.credentials
  }

  getCredentialStatus () {
    return this.credentials.status || {}
  }
  // Sets loader percentage
  setCredentials (data) {
    this.credentials = { ...this.credentials, ...data.credentials }
  }

  setCredentialStatus (data) {
    this.credentials = { ...this.credentials, status: { ...data.summary } }
  }
}

export default SatCredentialsStore.getInstance()
