import BaseStore from './baseStore'

class RestrictionsStore extends BaseStore {
  constructor () {
    super()
    this.remainingExcelDownloads = 0
    this.remainingEmails = 0
    this.remainingPdfDownloads = 0
    this.actions = {
      RESTRICTIONS_RECEIVED: action => {
        this.setRemainingDownloads(action.data.restrictions)
      },
      EXPORT_DOCUMENT: () => {
        this.exportDocument()
      },
      EMAIL_SEND: () => {
        this.emailSend()
      },
      PDF_DOWNLOADED: () => {
        this.pdfDownloaded()
      }
    }
  }

  emailSend () {
    this.remainingEmails = this.remainingEmails - 1
  }

  exportDocument () {
    this.remainingExcelDownloads = this.remainingExcelDownloads - 1
  }

  pdfDownloaded () {
    this.remainingPdfDownloads = this.remainingPdfDownloads - 1
  }

  setRemainingDownloads (data) {
    this.remainingExcelDownloads = data.excel_download
    this.remainingEmails = data.emails_send
    this.remainingPdfDownloads = data.invoices_download
  }

  getRemainingExcelDownloads () {
    return this.remainingExcelDownloads
  }

  getRemainingEmail () {
    return this.remainingEmails
  }

  getRemainingPdfDownload () {
    return this.remainingPdfDownloads
  }
}

export default RestrictionsStore.getInstance()
