// Libraries
import React, { Component } from 'react'
import { Link, withRouter } from 'react-router'
import pluralize from 'pluralize'
import AppSignal from 'appsignal'

// Components
import Paginator from 'shared/paginator'
import NotificationsActions from 'actions/notifications'
import Loading from 'shared/loading'
import { prettyDate, formatCurrency } from 'utils'

import API from 'api'

class InvoiceCalculation extends Component {
  state = {
    invoices: [],
    totalPages: 0,
    totalItems: 0,
    invoicesIds: [],
    requestInProgress: false
  }

  componentDidMount () {
    this.getInvoiceCalculation()
  }

  componentDidUpdate (prevProps) {
    if (prevProps.location.query.page !== this.props.location.query.page) {
      this.getInvoiceCalculation()
    }
  }

  // Get invoices calculation from api
  getInvoiceCalculation = async () => {
    const { calculationId, type } = this.props
    const { page } = this.props.location.query

    const params = {
      page: page,
      invoice_type: type,
      id: calculationId,
      stamped: true
    }
    this.setState({ requestInProgress: true })
    try {
      const {
        invoices,
        pagination: { total_pages: totalPages, total_items: totalItems }
      } = await API.InvoicesConsiderated.List(params)

      this.setState({
        invoices,
        totalPages: totalPages,
        totalItems: totalItems
      })
    } catch (error) {
      AppSignal.sendError(error)
      NotificationsActions.error(error)
    } finally {
      this.setState({ requestInProgress: false })
    }
  }

  renderContent () {
    const { invoices } = this.state
    return (
      <tbody>
        {invoices.map(invoice => {
          return (
            <tr key={invoice.id}>
              <td className='has-text-left small-padding'>
                <Link to={`/facturas/ver/${invoice.id}`}>
                  {invoice.legal_name}
                </Link>
              </td>
              <td className='is-uppercase small-padding'>{invoice.rfc}</td>
              <td className='is-uppercase small-padding'>
                {prettyDate(invoice.issue_date, '', true, false, '-')}
              </td>
              <td className='has-text-right small-padding'>
                {formatCurrency(invoice.total)}
              </td>
            </tr>
          )
        })}
      </tbody>
    )
  }

  // Main render
  render () {
    const { totalPages, totalItems, requestInProgress } = this.state
    const { location } = this.props
    return (
      <div className='section section--is-small'>
        <div className='columns is-centered'>
          <div className='column is-11'>
            <p className='caption'>
              {`Hay ${totalItems} ${pluralize('factura', totalItems)}
                ${pluralize('relacionada', totalItems)}`}
            </p>
            {requestInProgress ? (
              <Loading />
            ) : (
              <table className='table table--calculation-table is-borderless'>
                <thead>
                  <tr>
                    <th className='has-text-left padding-bottom-less'>
                      Concepto
                    </th>
                    <th className='padding-bottom-less'>RFC</th>
                    <th className='padding-bottom-less'>Fecha de emisi√≥n</th>
                    <th className='has-text-right padding-bottom-less'>
                      Total
                    </th>
                  </tr>
                </thead>
                {this.renderContent()}
              </table>
            )}
            <Paginator location={location} totalPages={totalPages} />
          </div>
        </div>
      </div>
    )
  }
}

export default withRouter(InvoiceCalculation)
