import React, { Component } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

export const TabList = props => {
  const children = React.Children.map(props.children, (child, index) => {
    return React.cloneElement(child, {
      isActive: index === props.activeIndex,
      onClick: () => props.onActivate(index)
    })
  })

  return (
    <div className='espacio-medium' role='tablist'>
      <div
        className={classNames('tabs', 'is-small', {
          'is-toggle': props.isToggle
        })}
      >
        <ul className={props.className}>{children}</ul>
      </div>
    </div>
  )
}

TabList.propTypes = {
  children: PropTypes.oneOfType([PropTypes.element, PropTypes.array]),
  isToggle: PropTypes.bool,
  activeIndex: PropTypes.number
}

export const Tab = props => (
  <li
    className={classNames({
      'is-active': props.isActive,
      'is-hidden': props.isHidden
    })}
    role='tab'
    aria-selected={props.isActive ? 'true' : 'false'}
    aria-disabled={!props.isActive ? 'true' : 'false'}
  >
    <button onClick={props.disabled ? null : props.onClick}>
      <span>{props.children}</span>
    </button>
  </li>
)

Tab.propTypes = {
  isActive: PropTypes.bool,
  disabled: PropTypes.bool,
  onClick: PropTypes.func,
  children: PropTypes.string
}

export const TabPanels = props => <div>{props.children[props.activeIndex]}</div>

TabPanels.propTypes = {
  children: PropTypes.oneOfType([PropTypes.element, PropTypes.array]),
  activeIndex: PropTypes.number
}

export const TabPanel = props => <div role='tabpanel'>{props.children}</div>

TabPanel.propTypes = {
  children: PropTypes.oneOfType([PropTypes.element, PropTypes.array])
}

class Tabs extends Component {
  constructor (props) {
    super(props)

    this.state = { activeIndex: 0 }
  }

  static TabList = TabList

  static Tab = Tab
  static TabPanels = TabPanels
  static TabPanel = TabPanel

  changeTab = activeIndex => {
    this.setState({ activeIndex })

    if (this.props.onTabChange) {
      this.props.onTabChange()
    }
  }

  render () {
    const children = React.Children.map(this.props.children, (child, index) => {
      if (child.type === TabPanels) {
        return React.cloneElement(child, {
          activeIndex: this.state.activeIndex
        })
      } else if (child.type === TabList) {
        return React.cloneElement(child, {
          activeIndex: this.state.activeIndex,
          onActivate: this.changeTab
        })
      } else {
        return child
      }
    })

    return <div>{children}</div>
  }
}

Tabs.propTypes = {
  children: PropTypes.oneOfType([PropTypes.element, PropTypes.array])
}

export default Tabs
