// Libraries
import React from 'react'
import PropTypes from 'prop-types'
// Components
import { TAX_CLASSIFICATIONS } from 'shared/catalogs'

// Render sigle item
const renderItem = (name, parent, item) => {
  let renderItem = null
  if (typeof parent[item] !== 'undefined' && parent[item] !== null) {
    renderItem = (
      <p>
        <label className='caption'>{name}</label>
        <br />
        <span className='body'>{parent[item]}</span>
      </p>
    )
  }
  return renderItem
}

// Render Issuer for invoice with version 3.2
const render32 = issuer => {
  let taxClassification = typeof issuer.tax_classifications !== 'undefined' &&
    issuer.tax_classifications !== null && (
    <p>
      <label className='caption'>Régimen Fiscal</label>
      <br />
      <span className='body'>{issuer.tax_classifications[0].name}</span>
    </p>
  )
  return (
    <div className='content'>
      <p>
        <span className='title'>{issuer.name}</span>
      </p>
      {renderItem('RFC', issuer, 'rfc')}
      {taxClassification}
    </div>
  )
}

// Render Issuer for invoice with version 3.3
const render33 = issuer => {
  let taxClassification = typeof issuer.tax_classification !== 'undefined' &&
    issuer.tax_classification !== null && (
    <p>
      <label className='caption'>Régimen Fiscal</label>
      <br />
      <span className='body'>
        {
          TAX_CLASSIFICATIONS.find(t => t.id === issuer.tax_classification)
            .label
        }
      </span>
    </p>
  )
  return (
    <div className='content'>
      <p>
        <span className='title'>{issuer.name}</span>
      </p>
      {renderItem('RFC', issuer, 'rfc')}
      {taxClassification}
    </div>
  )
}

const Issuer = ({ invoice }) => {
  let issuer = (
    <div className='block unselectable'>
      <p>No hay datos de emisor</p>
    </div>
  )
  switch (invoice.version) {
    case '3.2':
      issuer = render32(invoice.issuer)
      break
    case '3.3':
      issuer = render33(invoice.issuer)
      break
  }
  return (
    <div className='column is-half'>
      <h4 className='subheader-green'>Emisor</h4>
      {issuer}
    </div>
  )
}

Issuer.propTypes = {
  invoice: PropTypes.object
}

export default Issuer
