import React, { Component } from 'react'
import PropTypes from 'prop-types'

const INFINITE_TIME = 2147483647 // Es el numero máximo que permite el setTimeout - 1

class Notification extends Component {
  constructor () {
    super()
    this.handleClick = this.handleClick.bind(this)
    this.requestHide = this.requestHide.bind(this)
  }
  // Sets timer based on the props (default is 5000)
  componentDidMount () {
    const { timeOut } = this.props
    this.timer =
      timeOut === 'infinite'
        ? setTimeout(this.requestHide, INFINITE_TIME)
        : setTimeout(this.requestHide, timeOut)
  }

  // Clears timer to avoid memory leaks
  componentWillUnmount () {
    if (this.timer) clearTimeout(this.timer)
  }

  // Calls callback function when notification is clicked, then closes notification
  handleClick () {
    const { onClick } = this.props
    if (onClick) onClick()
    this.requestHide()
  }

  // Closes notification
  requestHide () {
    const { onRequestHide, id } = this.props
    if (onRequestHide) onRequestHide(id)
  }

  render () {
    let { type, message } = this.props
    // Changes string 'error' for 'danger' to match Bulma classes
    if (type === 'error') type = 'danger'

    return message && typeof message === 'string' ? (
      <div className={`notification is-${type}`} onClick={this.handleClick}>
        <i
          className='mdi mdi-close-circle-outline close'
          onClick={this.requestHide}
        />
        {message}
      </div>
    ) : null
  }
}

export default Notification

Notification.propTypes = {
  type: PropTypes.oneOf(['info', 'success', 'error']),
  message: PropTypes.oneOfType([PropTypes.object, PropTypes.string]),
  timeOut: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
  onClick: PropTypes.func,
  onRequestHide: PropTypes.func,
  id: PropTypes.string
}

Notification.defaultProps = {
  type: 'info',
  message: null,
  timeOut: 5000
}
