// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import moment from 'moment'
import accounting from 'accounting'
import { Link } from 'react-router'

import Paginator from 'shared/paginator'
import Datepicker from 'shared/datepicker'
import TextField from 'shared/textField'
import TableFilter from 'shared/tableFilter'
import Card from 'shared/card'
import { DATE_FORMAT, formatDateInvoice, prettyDate, resetScroll } from 'utils'

import SentEmailsActions from 'actions/facturasEnviadas'

import SentEmailsStore from 'stores/facturasEnviadas'

// Filter keys
const EMAIL_TO_KEY = 'to'
const START_DATE_KEY = 'start_date'
const END_DATE_KEY = 'end_date'

function getCurrentState () {
  let emailList = SentEmailsStore.getEmailList()
  let totalPages = SentEmailsStore.getTotalPages()
  let totalItems = SentEmailsStore.getTotalItems()
  return {
    emailList,
    totalItems,
    totalPages
  }
}

class FacturasEnviadas extends Component {
  constructor (props) {
    super(props)
    this.data = {
      filters: {
        [EMAIL_TO_KEY]: '',
        [START_DATE_KEY]: moment('2014-01-01', DATE_FORMAT),
        [END_DATE_KEY]: moment()
      }
    }
    this.state = Object.assign({}, this.data, getCurrentState())
    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    SentEmailsStore.addChangeListener(this._onChange)
    this.loadData()
    resetScroll()
  }

  componentDidUpdate (prevProps) {
    if (this.props.location.search !== prevProps.location.search) {
      this.loadData(this.props)
    }
  }

  componentWillUnmount () {
    // Removes the listener to avoid memory leaks
    SentEmailsStore.removeChangeListener(this._onChange)
  }

  _onChange () {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    let state = getCurrentState()
    this.setState(state)
  }

  loadData (props = this.props) {
    const params = Object.assign({}, props.location.query)
    SentEmailsActions.getEmailList(params)
    this.setState(prevState => ({
      filters: Object.assign({}, prevState.filters, params)
    }))
  }

  // Change filter values
  onChangeFilter (field, e) {
    const filter = { [field]: e._isAMomentObject ? e : e.target.value }
    this.setState(prevState => ({
      filters: Object.assign({}, prevState.filters, filter)
    }))
  }

  prepareFilters (filters) {
    return Object.assign({}, filters, {
      [START_DATE_KEY]: formatDateInvoice(filters[START_DATE_KEY]),
      [END_DATE_KEY]: formatDateInvoice(filters[END_DATE_KEY])
    })
  }

  clearFilters () {
    this.setState({ filters: this.data.filters })
  }

  // Render single email in list
  renderEmail (email) {
    const { id, to, message, created_at: createdAt } = email
    return (
      <tr key={id}>
        <td className='align-left'>
          <Link to={`/emails/ver/${id}`}>{to}</Link>
        </td>
        <td className='flexCell align-left'>{message}</td>
        <td className='align-right'>
          {prettyDate(createdAt, DATE_FORMAT, true, true, '/')}
        </td>
      </tr>
    )
  }

  // Render table with sent emails
  renderEmails () {
    const { emailList, totalPages } = this.state
    const { location } = this.props
    return emailList.length > 0 ? (
      <div className='is-fullwidth'>
        <table className='table'>
          <thead>
            <tr>
              <th className='align-left'>Para</th>
              <th className='align-left'>Mensaje</th>
              <th className='align-right'>Fecha de env√≠o</th>
            </tr>
          </thead>
          <tbody>{emailList.map(email => this.renderEmail(email))}</tbody>
        </table>
        <Paginator totalPages={totalPages} location={location} />
      </div>
    ) : (
      <div className='block unselectable'>
        <p>No hay correos</p>
      </div>
    )
  }

  // Render a wrapper for the filters element
  renderFilters () {
    const { filters } = this.state
    return (
      <TableFilter
        baseUrl='/emails'
        filters={filters}
        prepareFilters={this.prepareFilters.bind(this)}
        onClearFilters={this.clearFilters.bind(this)}
      >
        <TextField
          value={this.state.filters[EMAIL_TO_KEY]}
          label='Enviado a'
          placeholder='correo@ejemplo.com'
          onChange={this.onChangeFilter.bind(this, EMAIL_TO_KEY)}
        />
        <Datepicker
          value={this.state.filters[START_DATE_KEY]}
          label='Fecha inicial'
          placeholder='Fecha inicial'
          onChange={this.onChangeFilter.bind(this, START_DATE_KEY)}
        />
        <Datepicker
          value={this.state.filters[END_DATE_KEY]}
          label='Fecha final'
          placeholder='Fecha final'
          onChange={this.onChangeFilter.bind(this, END_DATE_KEY)}
        />
      </TableFilter>
    )
  }

  // Main render
  render () {
    return (
      <div>
        <h1 className='headline'>Facturas Enviadas</h1>
        <Card>
          {this.renderFilters()}
          <div className='caption'>
            {accounting.formatNumber(this.state.totalItems)} correos visibles
          </div>
          <br />
          {this.renderEmails()}
        </Card>
      </div>
    )
  }
}

FacturasEnviadas.propTypes = {
  location: PropTypes.object
}

export default FacturasEnviadas
