// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'

// Components
import Button from 'shared/button'
import { removeEmpty } from 'utils'

// Children
import Issuer from './issuer'
import Receiver from './receiver'
import EntityAddressees from './entityAddressees'
import GeneralInfo from './generalInfo'
import Merchandises from './merchandises'

class ForeignTrade extends Component {
  // Validate foreign trade children
  validate () {
    const generalInfo = this.refs['generalInfo'].validate()
    const issuer = this.refs['issuer'].validate()
    const receiver = this.refs['receiver'].validate()
    const entityAddressees = this.refs['entityAddressees'].validate()
    const merchandises = this.refs['merchandises'].validate()

    return [
      generalInfo,
      issuer,
      receiver,
      entityAddressees,
      merchandises
    ].every(v => v)
  }

  // Send data from Foreign trade to main invoice
  validateAndExport () {
    if (this.validate()) {
      const generalInfo = this.refs['generalInfo'].export()
      const issuer = this.refs['issuer'].export()
      const receiver = this.refs['receiver'].export()
      const entityAddressees = this.refs['entityAddressees'].export()
      const merchandises = this.refs['merchandises'].export()

      const topData = {}
      const lowData = Object.assign(
        { version: '1.1' },
        generalInfo,
        { issuer },
        { receiver },
        { addressees: entityAddressees },
        { merchandises: merchandises }
      )
      const foreignTrade = {
        foreign_trade: lowData,
        topData
      }
      let cleanForeignTrade = Object.assign({}, foreignTrade)
      return removeEmpty(cleanForeignTrade)
    } else {
      return false
    }
  }

  // Renders foreign trade form
  renderForm () {
    return (
      <div className='column'>
        <GeneralInfo ref='generalInfo' />
        <hr />
        <Issuer ref='issuer' />
        <hr />
        <Receiver ref='receiver' />
        <hr />
        <EntityAddressees ref='entityAddressees' />
        <hr />
        <Merchandises ref='merchandises' />
      </div>
    )
  }

  // Renders main foreign trade component
  render () {
    return (
      <div className='espacio-large'>
        <p className='content'>
          <span className='title'>{this.props.title}</span>
        </p>
        <div className='columns is-multiline'>{this.renderForm()}</div>
        <Button
          buttonStyle='dangerLink'
          onClick={() => {
            this.props.deleteAddOn(this.props.uuid)
          }}
          icon='delete'
        >
          Eliminar Complemento
        </Button>
      </div>
    )
  }
}

ForeignTrade.propTypes = {
  deleteAddOn: PropTypes.func,
  uuid: PropTypes.string,
  title: PropTypes.string
}

export default ForeignTrade
