import AppDispatcher from '../dispatcher'
import API from '../api'
import AppSignal from 'appsignal'

// This action creator sends the type of action and the params (if any) to the dispatcher
export default {
  getInvoices (params) {
    // Request the tax statements list to the API, then triggers the change on the store if success
    API.Invoice.List(params)
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'INVOICES_RECEIVED',
          data: response,
          invoiceType: params.invoice_type
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
      })
  },

  exportInvoices (params, format) {
    // Export invoices to desired format
    switch (format) {
      case 'invoice_excel':
        return API.Invoice.ExportInvoiceToExcel(params)
          .then(response => {
            AppDispatcher.handleAction({
              actionType: 'EXPORT_DOCUMENT',
              data: response
            })
          })
          .catch(error => {
            AppSignal.sendError(new Error(error))
            throw error
          })
      case 'payroll_excel':
        return API.Invoice.ExportPayrollToExcel(params).catch(error => {
          AppSignal.sendError(new Error(error))
          throw error
        })
      case 'payment_excel':
        return API.Invoice.ExportPaymentToExcel(params).catch(error => {
          AppSignal.sendError(new Error(error))
          throw error
        })
      case 'xml':
        return API.Invoice.ExportToXml(params).catch(error => {
          AppSignal.sendError(new Error(error))
          throw error
        })
    }
  },
  setFrequentlyUsedData () {
    return API.Invoice.GetUsedData()
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'USED_DATA_RECEIVED',
          data: response
        })
      })
      .catch(error => console.error(error))
  },
  setProductsCode (params) {
    return API.Invoice.GetProducts(params)
      .then(response => {
        return response
      })
      .catch(error => {
        throw error
      })
  },
  setUnitsCode (params) {
    return API.Invoice.GetUnits(params)
      .then(response => {
        return response
      })
      .catch(error => {
        throw error
      })
  },
  cancelInvoice (invoiceId) {
    return API.Invoice.Cancel(invoiceId)
  },
  async askCancelationStatus (invoiceId) {
    let cancelationStatus = {}

    try {
      let { in_progress: inProgress, cancelled } = await API.Invoice.AskStatus(
        invoiceId
      )
      cancelationStatus.inProgress = inProgress
      cancelationStatus.cancelled = cancelled
    } catch (error) {
      AppSignal.sendError(new Error('Ocurrió un error al cancelar la factura.'))
      cancelationStatus.progress = 'failed'
      cancelationStatus.cancelled = false
    }

    return cancelationStatus
  },
  getInvoiceInformation () {
    return API.Invoice.GetSerieAndFolio()
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'INVOICE_DATA_RECEIVED',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(new Error(error))
      })
  },

  sendEmail (invoiceId, data) {
    return API.Invoice.SendByEmail(invoiceId, data)
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'EMAIL_SEND',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(new Error(error))
        throw error
      })
  }
}
