// Libraries
import React from 'react'
import classNames from 'classnames'
import PropTypes from 'prop-types'

/**
 * Cards for inventory products.
 *
 * Is a card that could have:
 *  Title
 *  Body
 *  Unit
 *  Type of unit
 * @param {*} props
 */
const InventoryCard = props => {
  const { selectCard, text, body, unit, type, outline, inactive } = props
  return (
    <div
      className={classNames('inventory-card', {
        'inventory-card--selected': outline
      })}
      onClick={selectCard}
    >
      <div className={`inventory-card__body`}>
        <p
          className={classNames('inventory-card__title', {
            'inventory-card__title--selected': outline,
            'inventory-card__title--inactive': inactive
          })}
        >
          {text}
        </p>
        <p
          className={classNames({
            'inventory-card__body--selected': outline,
            'inventory-card__body--inactive': inactive
          })}
        >
          {body}
        </p>
      </div>
      <div
        className={classNames('inventory-card__unit', {
          'inventory-card__unit-background--selected inventory-card__unit--selected': outline
        })}
      >
        <p className='inventory-card__unit--stock'>{unit}</p>
        <p
          className={classNames('inventory-card__unit--type', {
            'inventory-card__unit--type--inactive': inactive
          })}
        >
          {type}
        </p>
      </div>
    </div>
  )
}

export default InventoryCard

InventoryCard.defaultProps = {
  selectCard: () => {},
  text: '',
  body: '',
  unit: 0,
  type: '',
  outline: false
}

InventoryCard.propTypes = {
  selectCard: PropTypes.func,
  text: PropTypes.string.isRequired,
  body: PropTypes.string.isRequired,
  unit: PropTypes.number.isRequired,
  outline: PropTypes.bool,
  type: PropTypes.string
}
