// Libraries
import React, { Component, Fragment } from 'react'
import { handleBack, handleInputs, parseProducts, parseUnits } from 'utils'
import { Async } from 'react-select'
import classNames from 'classnames'
import { browserHistory } from 'react-router'

// Components
import Button from 'shared/button'
import TextField from 'shared/textField'

// Actions
import InvoicesActions from 'actions/invoices'
import NotificationsActions from 'actions/notifications'
import InventoriesActions from 'actions/inventories'

// Stores
import InventoriesStore from 'stores/inventories'
import InvoicesStore from 'stores/invoices'

// Constants
const requiredFields = new Set()

export const createProduct = ({
  name,
  description,
  sku,
  unitCode,
  productCode,
  stock
}) => ({
  product: {
    name: name,
    description: description,
    sku: sku,
    unit_code: unitCode.value,
    prod_serv_code: productCode.value,
    stock: stock
  }
})

const getCurrentState = () => {
  return {
    hasProducts: InventoriesStore.getHasProducts(),
    products: InvoicesStore.getProducts()
  }
}

export default class SingleUploadProduct extends Component {
  state = {
    requestInProgress: false,
    name: '',
    description: '',
    productCode: '',
    unitCode: '',
    sku: '',
    stock: 1,
    ...getCurrentState()
  }

  // Adds listener, when the Store changes, trigger the _onChange callback func
  componentDidMount () {
    InvoicesStore.addChangeListener(this._onChange)
    InvoicesActions.setFrequentlyUsedData()
  }

  componentWillUnmount () {
    InvoicesStore.removeChangeListener(this._onChange)
    requiredFields.clear()
  }

  // When the store changes, the state goes to it and pulls the updated data
  // then sets it so the component renders
  _onChange = () => {
    this.setState(getCurrentState())
  }

  onChangeProductType = product => {
    this.setState({ productCode: product })
  }

  onChangeUnitType = unitCode => {
    this.setState({ unitCode: unitCode })
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeed, else false.
  validateAll () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate()) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  getProducts = value => {
    if (typeof value !== 'undefined' && value.length === 0) {
      return Promise.resolve(InvoicesStore.getProducts()).then(products => ({
        options: parseProducts(products)
      }))
    }

    return InvoicesActions.setProductsCode({ full_text: value })
      .then(data => {
        return { options: parseProducts(data) }
      })
      .catch(error => {
        NotificationsActions.error(error)
      })
  }

  getUnits = value => {
    if (typeof value !== 'undefined' && value.length === 0) {
      return Promise.resolve(InvoicesStore.getUnits()).then(units => ({
        options: parseUnits(units)
      }))
    }

    return InvoicesActions.setUnitsCode({ full_text: value })
      .then(data => {
        return { options: parseUnits(data) }
      })
      .catch(error => {
        NotificationsActions.error(error)
      })
  }

  saveProduct = () => {
    this.setState({ requestInProgress: true })
    if (this.validateAll()) {
      InventoriesActions.createProduct(createProduct(this.state))
        .then(() => {
          NotificationsActions.success('Producto agregado exitosamente')
          browserHistory.push('/inventarios/productos')
        })
        .catch(error => {
          NotificationsActions.error(error)
        })
        .finally(() => {
          this.setState({ requestInProgress: false })
        })
    } else {
      this.setState({ requestInProgress: false })
    }
  }

  render () {
    const {
      requestInProgress,
      hasProducts,
      name,
      description,
      productCode,
      unitCode,
      sku,
      stock
    } = this.state
    return (
      <Fragment>
        {hasProducts && (
          <div className='columns'>
            <div className='column is-12'>
              <Button
                buttonStyle='link'
                icon='chevron-double-left'
                onClick={handleBack}
              >
                Volver
              </Button>
            </div>
          </div>
        )}

        <form className='columns is-multiline'>
          <div className='column is-6'>
            <TextField
              value={name}
              onChange={handleInputs(this, 'name')}
              required
              label='Nombre'
              ref={r => requiredFields.add(r)}
            />
          </div>
          <div className='column is-6'>
            <TextField
              value={description}
              onChange={handleInputs(this, 'description')}
              required
              label='Descripción'
              ref={r => requiredFields.add(r)}
            />
          </div>
          <div className='column is-6'>
            <label className='caption'>{'Código de producto'} *</label>
            <div
              className={classNames('control has-info', {
                'select-required': productCode === null || productCode === ''
              })}
            >
              <Async
                value={productCode}
                placeholder={'Código de producto'}
                loadingPlaceholder='Buscando'
                searchPromptText='Teclear el nombre del producto'
                noResultsText='No encontrado'
                loadOptions={this.getProducts}
                onChange={this.onChangeProductType}
                ignoreAccents={false}
                clearable
                filterOptions={options => options}
                autoload
                cache={false}
              />
              <span className='icon info-icon'>
                <span className='tooltip'>
                  Ingresa una palabra que ayude a identificar tu producto o
                  servicio
                </span>
                <i className='mdi mdi-information-outline' />
              </span>
              <span
                className={classNames('help is-danger', {
                  'is-hidden': productCode !== null
                })}
              >
                Este campo es requerido
              </span>
            </div>
          </div>
          <div className='column is-6'>
            <div className='control'>
              <label className='caption'>{'Código de Unidad  - SAT'} *</label>
              <div
                className={classNames('control has-info', {
                  'select-required': unitCode === null || unitCode === ''
                })}
              >
                <Async
                  placeholder={'Código de Unidad  - SAT'}
                  loadingPlaceholder='Buscando'
                  searchPromptText='Teclear el nombre de la unidad'
                  noResultsText='No encontrado'
                  loadOptions={this.getUnits}
                  value={unitCode}
                  onChange={this.onChangeUnitType}
                  ignoreAccents={false}
                  clearable
                  filterOptions={options => options}
                  autoload
                  cache={false}
                />
                <span className='icon info-icon'>
                  <span className='tooltip'>
                    Unidad de medida del producto o servicio
                  </span>
                  <i className='mdi mdi-information-outline' />
                </span>
                <span
                  className={classNames('help is-danger', {
                    'is-hidden': unitCode !== null
                  })}
                >
                  Este campo es requerido
                </span>
              </div>
            </div>
          </div>
          <div className='column is-6'>
            <TextField
              value={sku}
              onChange={handleInputs(this, 'sku')}
              required
              placeholder='Número identificador'
              label='SKU'
              ref={r => requiredFields.add(r)}
            />
          </div>
          <div className='column is-3'>
            <TextField
              value={stock}
              onChange={handleInputs(this, 'stock')}
              required
              label='Existencias'
              type='number'
              ref={r => requiredFields.add(r)}
            />
          </div>
        </form>
        <div className='columns'>
          <div className='column is-12'>
            <p className='caption required'>* Campos obligatorios</p>
          </div>
        </div>
        <div className='buttons-bar'>
          {hasProducts && (
            <Button icon='cancel' onClick={handleBack}>
              Cancelar
            </Button>
          )}
          <Button
            buttonStyle='primary'
            icon='content-save'
            onClick={this.saveProduct}
            loading={requestInProgress}
            disabled={requestInProgress}
          >
            Guardar aprobados
          </Button>
        </div>
      </Fragment>
    )
  }
}
