// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import update from 'immutability-helper'

// Components
import Button from 'shared/button'
import SelectField from 'shared/selectField'
import { removeEmpty } from 'utils'

// Children
import Ordinary from './ordinary'
import PreCampaign from './preCampaign'
import Campaign from './campaign'

// Constants
const PROCESS_KIND_ORDINARY = 'Ordinario'
const PROCESS_KIND_PRECAMPAIGN = 'Precampa単a'
const PROCESS_KIND_CAMPAIGN = 'Campa単a'

const PROCESS_KINDS = [
  { id: PROCESS_KIND_ORDINARY, label: PROCESS_KIND_ORDINARY },
  { id: PROCESS_KIND_PRECAMPAIGN, label: PROCESS_KIND_PRECAMPAIGN },
  { id: PROCESS_KIND_CAMPAIGN, label: PROCESS_KIND_CAMPAIGN }
]

// Keys
const PROCESS_KIND_KEY = 'process_type'
const INE_KEY = 'ine_concept_mg'
const PROCESS_LOW_DATA_KEY = 'ine_entity_mgs'

class Ine extends Component {
  constructor (props) {
    super(props)
    this.data = {
      [PROCESS_KIND_KEY]: PROCESS_KINDS[0].id
    }
    this.state = Object.assign({}, this.data)
  }

  // Set target value to this.state[field]
  onChange (field, e) {
    this.setState({
      [field]: update(this.state[field], { $set: e.target.value })
    })
  }

  // Send data from INE to main invoice
  validateAndExport () {
    let { topData = {}, lowData = [] } = this.refs['process'].exportData()
    if (topData !== null && lowData !== null) {
      let ine = {
        [INE_KEY]: Object.assign(
          { [PROCESS_LOW_DATA_KEY]: lowData },
          { [PROCESS_KIND_KEY]: this.state[PROCESS_KIND_KEY] },
          topData
        )
      }
      let cleanIne = Object.assign({}, ine)
      cleanIne = removeEmpty(cleanIne)
      return cleanIne
    } else {
      return false
    }
  }

  // Main render
  render () {
    let processKinds = {
      [PROCESS_KIND_ORDINARY]: <Ordinary ref='process' />,
      [PROCESS_KIND_PRECAMPAIGN]: <PreCampaign ref='process' />,
      [PROCESS_KIND_CAMPAIGN]: <Campaign ref='process' />
    }
    return (
      <div className='espacio-large'>
        <p className='content'>
          <span className='title' onClick={this.validateAndExport.bind(this)}>
            {this.props.title}
          </span>
        </p>
        <div className='columns is-multiline'>
          <div className='column is-12'>
            <SelectField
              label='Tipo de Proceso'
              placeholder='Seleccione un tipo'
              options={PROCESS_KINDS}
              value={this.state[PROCESS_KIND_KEY]}
              onChange={this.onChange.bind(this, PROCESS_KIND_KEY)}
            />
          </div>
          <div className='column is-12'>
            {processKinds[this.state[PROCESS_KIND_KEY]]}
          </div>
        </div>
        <Button
          buttonStyle='dangerLink'
          onClick={() => {
            this.props.deleteAddOn(this.props.uuid)
          }}
          icon='delete'
        >
          Eliminar Complemento
        </Button>
      </div>
    )
  }
}

Ine.propTypes = {
  title: PropTypes.string,
  deleteAddOn: PropTypes.func,
  uuid: PropTypes.string
}

export default Ine
