// Libraries
import React from 'react'
import PropTypes from 'prop-types'
import accounting from 'accounting'

// Render Concepts for invoice with version 3.2
const renderPerceptionsTable = (detail, conceptsKey) => {
  let renderConcepts = null
  if (detail[conceptsKey].length > 0) {
    renderConcepts = detail[conceptsKey].map((concept, index) => {
      return (
        <div className='viewInvoiceTable--row' key={index}>
          <div className='viewInvoiceTable--about'>{concept.concept}</div>
          <div className='viewInvoiceTable--unit'>
            {accounting.formatMoney(concept.taxed_amount_cents / 100, '', 2)}
          </div>
          <div className='viewInvoiceTable--pricePerUnit'>
            {accounting.formatMoney(concept.exempt_amount_cents / 100, '', 2)}
          </div>
          <div className='viewInvoiceTable--subTotal'>
            {accounting.formatMoney(
              (concept.taxed_amount_cents + concept.exempt_amount_cents) / 100,
              '',
              2
            )}
          </div>
        </div>
      )
    })
  }
  return (
    <div className='viewInvoiceTable'>
      <div className='viewInvoiceTable--row viewInvoiceTable--head'>
        <div className='viewInvoiceTable--about'>Descripción</div>
        <div className='viewInvoiceTable--unit'>Importe Gravado</div>
        <div className='viewInvoiceTable--pricePerUnit'>Importe Exento</div>
        <div className='viewInvoiceTable--subTotal'>Total</div>
      </div>
      {renderConcepts}
    </div>
  )
}

const renderDeductionsTable = (detail, conceptsKey) => {
  let renderConcepts = null
  if (detail[conceptsKey].length > 0) {
    renderConcepts = detail[conceptsKey].map((concept, index) => {
      return (
        <div className='viewInvoiceTable--row' key={index}>
          <div className='viewInvoiceTable--deductionAbout'>
            {concept.concept}
          </div>
          <div className='viewInvoiceTable--subTotal'>
            {accounting.formatMoney(concept.amount_cents / 100, '', 2)}
          </div>
        </div>
      )
    })
  }
  return (
    <div className='viewInvoiceTable'>
      <div className='viewInvoiceTable--row viewInvoiceTable--head'>
        <div className='viewInvoiceTable--deductionAbout'>Descripción</div>
        <div className='viewInvoiceTable--subTotal'>Importe</div>
      </div>
      {renderConcepts}
    </div>
  )
}

const renderOtherPaymentsTable = detail => {
  let renderConcepts = null
  if (detail.length > 0) {
    renderConcepts = detail.map((row, index) => {
      return (
        <div className='viewInvoiceTable--row' key={index}>
          <div className='viewInvoiceTable--deductionAbout'>{row.concept}</div>
          <div className='viewInvoiceTable--subTotal'>
            {accounting.formatMoney(row.amount_cents / 100, '', 2)}
          </div>
        </div>
      )
    })
  }
  return (
    <div className='viewInvoiceTable'>
      <div className='viewInvoiceTable--row viewInvoiceTable--head'>
        <div className='viewInvoiceTable--deductionAbout'>Descripción</div>
        <div className='viewInvoiceTable--subTotal'>Importe</div>
      </div>
      {renderConcepts}
    </div>
  )
}

const renderTotals = payroll => {
  let perceptionsTotal = payroll.perception_detail
    ? payroll.perception_detail.total_taxed_amount_cents +
      payroll.perception_detail.total_exempt_amount_cents
    : 0
  let deductionsTotal = payroll.deduction_detail
    ? payroll.deduction_detail.total_others_amount_cents +
      payroll.deduction_detail.total_tax_retained_cents
    : 0
  const otherPaymentsTotal = payroll.other_payments
    ? payroll.other_payments.reduce(
      (total, payment) => total + payment.amount_cents,
      0
    )
    : 0
  const total = perceptionsTotal + otherPaymentsTotal - deductionsTotal

  return (
    <div className='columns'>
      <div className='column is-offset-7'>
        <table className='table'>
          <tfoot>
            <tr>
              <td className='align-left'>&nbsp;TOTAL PERCEPCIONES:</td>
              <td className='has-text-right'>
                {accounting.formatMoney(perceptionsTotal / 100, '$ ')}
              </td>
            </tr>
            {payroll.other_payments && (
              <tr>
                <td className='align-left'>&nbsp;TOTAL OTROS PAGOS:</td>
                <td className='has-text-right'>
                  {accounting.formatMoney(otherPaymentsTotal / 100, '$ ')}
                </td>
              </tr>
            )}
            <tr>
              <td className='align-left'>&nbsp;TOTAL DEDUCCIONES:</td>
              <td className='has-text-right'>
                {accounting.formatMoney(deductionsTotal / 100, '$ ')}
              </td>
            </tr>
            <tr>
              <td className='align-left'>&nbsp;TOTAL:</td>
              <td className='has-text-right'>
                {accounting.formatMoney(total / 100, '$ ')}
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
    </div>
  )
}

const PayrollConcepts = props => {
  let { payroll } = props.invoice
  // Main render
  return (
    <div className='column'>
      {payroll.perception_detail ? (
        <div>
          <h4 className='subheader-green'>Percepciones</h4>
          <div className='columns'>
            <div className='column'>
              {renderPerceptionsTable(payroll.perception_detail, 'perceptions')}
            </div>
          </div>
          <br />
        </div>
      ) : null}
      {payroll.deduction_detail ? (
        <div>
          <h4 className='subheader-green'>Deducciones</h4>
          <div className='columns'>
            <div className='column'>
              {renderDeductionsTable(payroll.deduction_detail, 'deductions')}
            </div>
          </div>
          <br />
        </div>
      ) : null}
      {payroll.other_payments ? (
        <div>
          <h4 className='subheader-green'>Otros Pagos</h4>
          <div className='columns'>
            <div className='column'>
              {renderOtherPaymentsTable(payroll.other_payments)}
            </div>
          </div>
          <br />
        </div>
      ) : null}
      <div>
        <h4 className='subheader-green'>Total</h4>
        <div className='columns'>
          <div className='column'>{renderTotals(payroll)}</div>
        </div>
      </div>
    </div>
  )
}

PayrollConcepts.propTypes = {
  invoice: PropTypes.object
}
export default PayrollConcepts
