// Libraries
import React, { Fragment } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

/**
 * These are the headers for tableCardBody component
 * @param {*} props
 */
const TableCardHeader = props => {
  const { handleArrows, arrows, info } = props

  return (
    <Fragment>
      <div className='table-card--row'>
        {info.map(({ header, id, hasIcon }) => (
          <div
            className={classNames('table-card__header', {
              'table-card__header--pink': arrows === id
            })}
          >
            <span onClick={handleArrows(id)}>{header}</span>
            {hasIcon && (
              <i
                className={classNames('mdi ', {
                  'mdi-chevron-down': arrows === id,
                  'mdi-unfold-more-horizontal': arrows !== id
                })}
              />
            )}
          </div>
        ))}
      </div>
    </Fragment>
  )
}

export default TableCardHeader

TableCardHeader.defaultProps = {
  arrows: '',
  handleArrows: () => {},
  info: []
}

TableCardHeader.propTypes = {
  arrows: PropTypes.string,
  handleArrows: PropTypes.func.isRequired,
  info: PropTypes.array.isRequired
}
