import AppDispatcher from '../dispatcher'
import API from '../api'
import AppSignal from 'appsignal'

export default {
  getCredentialsStatus () {
    return API.EditClient.Summary()
      .then(response =>
        AppDispatcher.handleAction({
          actionType: 'SAT_CREDENTIALS_RECEIVED_STATUS',
          data: response
        })
      )
      .catch(error => {
        AppSignal.sendError(error)
        throw error
      })
  },
  getSatCredentials () {
    return API.EditClient.SatCredentials()
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'SAT_CREDENTIALS_RECEIVED',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
        throw error
      })
  },

  updateSatCredentials (data) {
    return API.EditClient.EditEntity(data)
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'SAT_CREDENTIALS_UPDATED',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
        throw error
      })
  }
}
