// Imports
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import update from 'immutability-helper'
import uuid from 'uuid'

// Components
import Button from 'shared/button'
import Modal from 'shared/modal'
import SelectField from 'shared/selectField'

// Invoice add-ons
import Ine from './ine'
import LocalTaxes from './localTaxes'
import ForeignTrade from './foreignTrade'

const addOns = [
  {
    title: 'INE - Instituto Nacional Electoral',
    component: Ine,
    totals: false,
    category: 'ine'
  },
  {
    title: 'Otros Derechos e Impuestos',
    component: LocalTaxes,
    totals: true,
    category: 'local_tax'
  },
  {
    title: 'Comercio Exterior',
    component: ForeignTrade,
    totals: false,
    category: 'foreign_trade'
  }
]

const ADD_ONS_KEY = 'addOns'

class AddOns extends Component {
  constructor () {
    super()
    this.data = {
      create: addOns[0].title,
      showModal: false,
      [ADD_ONS_KEY]: []
    }
    this.state = Object.assign({}, this.data)
  }

  // Updates an input's value when it changes
  onChange (field, e) {
    this.setState({ [field]: e.target.value })
  }

  // Handle changes to state
  toggleCreateAddon () {
    this.setState({ showModal: !this.state.showModal })
  }

  // Handle creation of a new add-on
  addAddOn () {
    const currentAddOns = this.state[ADD_ONS_KEY]
    const newAddOn = Object.assign(
      {},
      { uuid: uuid() },
      addOns.find(a => a.title === this.state.create)
    )
    this.setState(
      {
        [ADD_ONS_KEY]: update(currentAddOns, {
          [currentAddOns.length]: { $set: newAddOn }
        }),
        showModal: false
      },
      () => {
        this.props.onSelectAddon(newAddOn.category)
        this.setState({
          create: this.availableAddOns()[0].title
        })
      }
    )
  }

  // Remove add-on
  deleteAddOn (uuid) {
    this.setState(
      {
        [ADD_ONS_KEY]: this.state[ADD_ONS_KEY].filter(c => c.uuid !== uuid)
      },
      () => {
        this.props.onSelectAddon('regular')
      }
    )
  }

  // Get the list of available add ons, depending on the current selection
  availableAddOns () {
    return addOns.filter(
      addOn =>
        this.state[ADD_ONS_KEY].filter(a => a.category === addOn.category)
          .length === 0
    )
  }

  // Handle changes to invoice totals
  totalChanged (totals) {
    this.state[ADD_ONS_KEY].filter(c => c.totals).map(a =>
      this.refs[a.uuid].totalChanged(totals)
    )
  }

  // Render single add-ons
  // TODO: only send totals to components that require it
  renderAddOn (addOn) {
    const Cell = addOn.component
    return (
      <Cell
        key={addOn.uuid}
        ref={addOn.uuid}
        uuid={addOn.uuid}
        title={addOn.title}
        totals={this.props.totals}
        deleteAddOn={this.deleteAddOn.bind(this)}
      />
    )
  }

  // Call validations on all add-ons and return true if all validations succeed
  validateAllAddOns () {
    return this.state[ADD_ONS_KEY].map(addOn =>
      this.refs[addOn.uuid].validateAndExport()
    ).every(e => e)
  }

  // Export all add-ons
  exportAllAddOns () {
    return this.state[ADD_ONS_KEY].map(addOn =>
      this.refs[addOn.uuid].validateAndExport()
    )
  }

  // Render all add-ons
  renderAddOns () {
    const addOns = this.state[ADD_ONS_KEY]
    return addOns.length > 0
      ? addOns.map(addOn => this.renderAddOn(addOn))
      : null
  }

  // Render select to create new add-ons
  renderCreate () {
    return (
      <Modal
        isActive={this.state.showModal}
        toggleModal={this.toggleCreateAddon.bind(this)}
        onSubmit={this.addAddOn.bind(this)}
        title='Agregar Complemento'
      >
        <SelectField
          label='Tipo de Complemento'
          placeholder='Selecciona complemento'
          options={this.availableAddOns()}
          value={this.state.create}
          onChange={this.onChange.bind(this, 'create')}
          optionsConfig={{ id: 'title', label: 'title' }}
        />
      </Modal>
    )
  }

  // Render addons box only if there's at least 1 add-on
  renderAllAddOns () {
    return this.state[ADD_ONS_KEY].length > 0 ? (
      <div className='column is-12'>{this.renderAddOns()}</div>
    ) : null
  }

  // Renders the add addon button
  renderCreateButton () {
    if (this.state[ADD_ONS_KEY].length > 0) return null
    if (this.availableAddOns().length === 0) return null
    return (
      <Button
        buttonStyle='link'
        icon='plus'
        onClick={this.toggleCreateAddon.bind(this)}
      >
        Agregar Complemento
      </Button>
    )
  }

  // Main render
  render () {
    return (
      <div className='columns is-multiline'>
        <div className='column is-12'>
          <h2 className='subheader-green'>Complementos</h2>
          <p className='espacio-small caption'>
            Los complementos son opcionales, presiona el bot√≥n Agregar
            Complemento para iniciar.
          </p>
        </div>
        {this.renderAllAddOns()}
        <div className='column is-12'>
          {this.renderCreate()}
          {this.renderCreateButton()}
        </div>
      </div>
    )
  }
}

AddOns.propTypes = {
  onSelectAddon: PropTypes.func,
  totals: PropTypes.oneOfType([PropTypes.array, PropTypes.object])
}

export default AddOns
