import React from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

import Button from 'shared/button'

const Modal = props => {
  const footer = !props.noFooter ? (
    <footer className='modal-card-foot'>
      <Button onClick={props.toggleModal} icon={props.secondaryIcon}>
        {props.secondaryButton}
      </Button>
      <Button
        buttonStyle='primary'
        onClick={props.onSubmit}
        icon={props.primaryIcon}
        loading={props.loading}
        disabled={props.disabled}
      >
        {props.primaryButton}
      </Button>
    </footer>
  ) : null
  return (
    <div
      className={classNames('modal', {
        'is-active': props.isActive,
        'is-alert': props.isAlert
      })}
    >
      <div className='modal-background' onClick={props.toggleModal} />
      <div
        className={classNames(`modal-card ${props.modalClassName}`, {
          'modal-card--is-bigger': props.isBigger,
          'modal-card--is-full-width': props.isFullScreen
        })}
      >
        <header
          className={classNames('modal-card-head', {
            'modal-card-head--promo-head': props.isAlert,
            'modal-card-head--no-background': props.noBackground
          })}
        >
          <p className='modal-card-title'>{props.title}</p>
          <i
            className='mdi mdi-close-circle-outline is-clickable'
            onClick={props.toggleModal}
          />
        </header>
        <section
          className={classNames('modal-card-body', {
            'modal--nofooter': props.noFooter,
            'modal-card-body--no-background': props.noBackground
          })}
        >
          {props.children}
        </section>
        {footer}
      </div>
    </div>
  )
}

export default Modal

Modal.propTypes = {
  isActive: PropTypes.bool.isRequired,
  toggleModal: PropTypes.func.isRequired,
  onSubmit: PropTypes.func.isRequired,
  title: PropTypes.string,
  noFooter: PropTypes.bool,
  loading: PropTypes.bool,
  disabled: PropTypes.bool,
  primaryButton: PropTypes.string,
  secondaryButton: PropTypes.string,
  secondaryIcon: PropTypes.string,
  primaryIcon: PropTypes.string,
  children: PropTypes.element
}

Modal.defaultProps = {
  isActive: false,
  onSubmit: () => {},
  title: '',
  noFooter: false,
  primaryButton: 'Aceptar',
  primaryIcon: 'check',
  secondaryButton: 'Cancelar',
  secondaryIcon: 'cancel'
}
