// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { resetScroll, askRemainingPlanDays } from 'utils'
// Components
import Card from 'shared/card'

// Children
import Rubros from './rubros'
import Rankings from './rankings'
import ItemsBehaviors from './itemsBehaviors'
import Behaviors from './behaviors'

// Tabs for Card
const TABS = [
  {
    id: 'historico',
    label: 'Comportamiento histórico',
    component: <Behaviors />
  },
  { id: 'rubros', label: 'Ingresos / Gastos por rubros', component: <Rubros /> },
  {
    id: 'rankings',
    label: 'Rankings',
    component: <Rankings />
  },
  {
    id: 'comportamientos',
    label: 'Comportamientos por rubros',
    component: <ItemsBehaviors />
  }
]

class Reports extends Component {
  componentDidMount () {
    resetScroll()
    askRemainingPlanDays()
  }
  // Main render
  render () {
    const { tab: currentTab } = this.props.route
    return (
      <div>
        <h1 className='headline'>Reportes</h1>
        <div className='columns'>
          <div className='column'>
            <Card title={TABS} activeTab={currentTab} baseUrl='/reportes'>
              {TABS.find(tab => tab.id === currentTab).component}
            </Card>
          </div>
        </div>
      </div>
    )
  }
}

Reports.propTypes = {
  route: PropTypes.shape({
    tab: PropTypes.string
  })
}

export default Reports
