// Libraries
import React from 'react'
import PropTypes from 'prop-types'

/**
 * Enconta Radio Button
 *
 * If you want to render many radio buttons, you should use index prop to avoid React warning.
 * @param {*} props
 */
const RadioButton = props => {
  const { value, index, label, checked, handleRadio } = props
  return (
    <label className='radio-button' htmlFor={value} key={index}>
      {label}
      <input
        id={value}
        type='radio'
        value={value}
        checked={checked}
        onChange={handleRadio}
      />
      <span className='radio-button__checkmark' />
    </label>
  )
}

export default RadioButton

RadioButton.propTypes = {
  value: PropTypes.string.isRequired,
  index: PropTypes.number.isRequired,
  label: PropTypes.string.isRequired,
  checked: PropTypes.bool.isRequired,
  handleRadio: PropTypes.func.isRequired
}

RadioButton.defaultProps = {
  value: '',
  index: '',
  label: '',
  checked: false,
  handleRadio: () => {}
}
