import React, { Component } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'
import clipboard from 'clipboard-polyfill'
import update from 'immutability-helper'
import validator from 'validator'

import NotificationsActions from 'actions/notifications'
import SatCredentialsActions from 'actions/satCredentials'
import SessionStore from 'stores/session'
import SatCredentialsStore from 'stores/satCredentials'
import SelectFile from 'shared/selectFile'
import TextField from 'shared/textField'
import Button from 'shared/button'

function getCurrentState () {
  let currentEntity = SessionStore.getCurrentEntity()
  let credentials = SatCredentialsStore.getCredentials()
  const user = SessionStore.getUser()
  return {
    currentEntity,
    credentials,
    user
  }
}

class Csd extends Component {
  constructor () {
    super()
    this.data = {
      requestInProgress: false,
      isCsdKeyModalOpen: false,
      isCsdCerModalOpen: false,
      csd: {
        key_file_password: '',
        key_file: {
          attached_file: '',
          original_filename: '',
          content_type: ''
        },
        cer_file: {
          attached_file: '',
          original_filename: '',
          content_type: ''
        }
      },
      isValid: {
        key_file_password: true,
        key_file: true,
        cer_file: true
      }
    }
    this.state = Object.assign({}, this.data, getCurrentState())
    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    SatCredentialsStore.addChangeListener(this._onChange)
  }

  componentWillUnmount () {
    // Removes the listener to avoid memory leaks
    SatCredentialsStore.removeChangeListener(this._onChange)
  }

  _onChange () {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    let state = getCurrentState()
    this.setState(state)
  }

  // Persist password to state
  onChangeCsdPassword (e) {
    this.setState({
      csd: update(this.state.csd, {
        key_file_password: { $set: e.target.value }
      })
    })
  }

  // Toggle key modal
  toggleCsdKeyModal () {
    this.setState({ isCsdKeyModalOpen: !this.state.isCsdKeyModalOpen })
  }

  // Toggle certificate modal
  toggleCsdCerModal () {
    this.setState({ isCsdCerModalOpen: !this.state.isCsdCerModalOpen })
  }

  // Persist key to state
  setCsdKey (file, filename, mime) {
    let key = {
      attached_file: file,
      original_filename: filename,
      content_type: mime
    }
    this.setState({
      csd: update(this.state.csd, { key_file: { $set: key } })
    })
  }

  // Persist certificate to state
  setCsdCer (file, filename, mime) {
    let cer = {
      attached_file: file,
      original_filename: filename,
      content_type: mime
    }
    this.setState({
      csd: update(this.state.csd, { cer_file: { $set: cer } })
    })
  }

  // Validate non empty key
  isValidCsdKey () {
    let isValid = !validator.isEmpty(this.state.csd.key_file.attached_file)
    this.setState({
      isValid: update(this.state.isValid, { key_file: { $set: isValid } })
    })
    return isValid
  }

  // Validate non empty certificate
  isValidCsdCer () {
    let isValid = !validator.isEmpty(this.state.csd.cer_file.attached_file)
    this.setState({
      isValid: update(this.state.isValid, { cer_file: { $set: isValid } })
    })
    return isValid
  }

  // Validate non empty password
  isValidCsdPassword () {
    let isValid = !validator.isEmpty(this.state.csd.key_file_password)
    this.setState({
      isValid: update(this.state.isValid, {
        key_file_password: { $set: isValid }
      })
    })
    return isValid
  }

  uploadCsd (e) {
    e.preventDefault()
    if (
      this.isValidCsdKey() &&
      this.isValidCsdCer() &&
      this.isValidCsdPassword()
    ) {
      this.setState({ requestInProgress: true }, () => {
        SatCredentialsActions.updateSatCredentials({
          taxable_entity: this.state.csd
        })
          .then(() => {
            NotificationsActions.success('CSD guardado correctamente')
            this.setState({ requestInProgress: false })
            SatCredentialsActions.getSatCredentials()
          })
          .catch(error => {
            NotificationsActions.error(error)
            this.setState({ requestInProgress: false })
          })
      })
    }
  }

  render () {
    let hidden =
      this.props.hideIfFull &&
      this.state.credentials.csd &&
      this.state.credentials.csd.key_file &&
      this.state.credentials.csd.cer_file &&
      this.state.credentials.csd.password
    const { user } = this.state
    return (
      <div className={classNames({ 'is-hidden': hidden })}>
        <h5 className='subtitle is-6 is-marginless'>CSD</h5>
        <div className='columns'>
          <div className='column'>
            <div className='field'>
              <TextField
                label='Contrase√±a del CSD'
                placeholder='Contrase√±a del CSD'
                type='password'
                className={classNames('input', {
                  'is-danger': !this.state.isValid.key_file_password
                })}
                onChange={this.onChangeCsdPassword.bind(this)}
                value={this.state.csd.key_file_password}
              />
              {user.role !== 'sales' && (
                <a
                  onClick={() => {
                    clipboard
                      .writeText(this.state.credentials.csd.password)
                      .then(
                        NotificationsActions.success(
                          'Contrase√±a copiada al portapapeles'
                        )
                      )
                  }}
                  className={classNames({
                    'is-hidden': !this.state.credentials.csd.password
                  })}
                >
                  Copiar contrase√±a almacenada
                </a>
              )}
            </div>
          </div>
          <div className='column'>
            <div className='field'>
              <label className='caption'>Extensi√≥n .key</label>
              <Button
                buttonStyle='info'
                onClick={this.toggleCsdKeyModal.bind(this)}
                className='is-fullwidth'
                icon={
                  this.state.csd.key_file.original_filename
                    ? 'file-outline'
                    : 'plus'
                }
              >
                {this.state.csd.key_file.original_filename ||
                  'Seleccionar Archivo'}
              </Button>
              <p
                className={classNames('help is-danger', {
                  'is-hidden': this.state.isValid.key_file
                })}
              >
                * Campo requerido
              </p>

              {user.role !== 'sales' && (
                <a
                  className={classNames({
                    'is-hidden': !this.state.credentials.csd.key_file
                  })}
                  href={this.state.credentials.csd.key_file}
                  download
                >
                  Descargar archivo key almacenado
                </a>
              )}
            </div>
          </div>
          <div className='column'>
            <div className='field'>
              <label className='caption'>Extensi√≥n .cer</label>
              <Button
                buttonStyle='info'
                className='is-fullwidth'
                onClick={this.toggleCsdCerModal.bind(this)}
                icon={
                  this.state.csd.cer_file.original_filename
                    ? 'file-outline'
                    : 'plus'
                }
              >
                {this.state.csd.cer_file.original_filename ||
                  'Seleccionar Archivo'}
              </Button>
              <p
                className={classNames('help is-danger', {
                  'is-hidden': this.state.isValid.cer_file
                })}
              >
                * Campo requerido
              </p>

              {user.role !== 'sales' && (
                <a
                  className={classNames({
                    'is-hidden': !this.state.credentials.csd.cer_file
                  })}
                  href={this.state.credentials.csd.cer_file}
                  download
                >
                  Descargar archivo .cer almacenado
                </a>
              )}
            </div>
          </div>
        </div>
        <Button
          className='align-right'
          buttonStyle='primary'
          icon='content-save'
          loading={this.state.requestInProgress}
          onClick={this.uploadCsd.bind(this)}
        >
          Guardar CSD
        </Button>
        <SelectFile
          isActive={this.state.isCsdKeyModalOpen}
          toggleModal={this.toggleCsdKeyModal.bind(this)}
          title='Archivo .key del CSD'
          fileTypePlaceholder='Extensi√≥n .key del CSD'
          setFile={this.setCsdKey.bind(this)}
          accept={'.key'}
        />
        <SelectFile
          isActive={this.state.isCsdCerModalOpen}
          toggleModal={this.toggleCsdCerModal.bind(this)}
          title='Archivo .cer del CSD'
          fileTypePlaceholder='Extensi√≥n .cer del CSD'
          setFile={this.setCsdCer.bind(this)}
          accept={'.cer'}
        />
      </div>
    )
  }
}

export default Csd

Csd.propTypes = {
  hideIfFull: PropTypes.bool
}

Csd.defaultProps = {
  hideIfFull: false
}
