import AppDispatcher from '../dispatcher'
import API from '../api'
import AppSignal from 'appsignal'

// This action creator sends the type of action and the params (if any) to the dispatcher
export default {
  create (account) {
    // Request the payment accounts list to the API, then triggers the change on the store if success
    return API.PaymentAccounts.Create(account.data).catch(error => {
      AppSignal.sendError(error)
      throw error
    })
  },

  update (account) {
    // Request the payment accounts list to the API, then triggers the change on the store if success
    return API.PaymentAccounts.Update(account.id, account.data).catch(error => {
      AppSignal.sendError(error)
      throw error
    })
  },

  getList (params) {
    // Request the payment accounts list to the API, then triggers the change on the store if success
    API.PaymentAccounts.List(params)
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'PAYMENT_ACCOUNTS_LIST_RECEIVED',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
      })
  },

  toggleStatus (id) {
    // Request the payment accounts list to the API, then triggers the change on the store if success
    return API.PaymentAccounts.ToggleStatus(id)
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'PAYMENT_ACCOUNTS_LIST_STATUS_CHANGED',
          data: response
        })
        return { message: 'Estatus actualizado' }
      })
      .catch(() => {
        return { message: 'Hubo un error al actualizar el estado' }
      })
  },

  getPaymentAccount (id) {
    API.PaymentAccounts.GetPaymentAccount(id).then(response => {
      AppDispatcher.handleAction({
        actionType: 'PAYMENT_ACCOUNT_RECEIVED',
        data: response
      })
    })
  },

  // Deletes the payment acount with the passed id
  deletePaymentAccount (id) {
    return API.PaymentAccounts.Delete(id)
  },

  // Resets the current document to its defaults in the store
  clearCurrent () {
    AppDispatcher.handleAction({
      actionType: 'CLEAR_CURRENT_PAYMENT_ACCOUNT'
    })
  },
  getBanksList () {
    // Request to get banks list
    return API.PaymentAccounts.GetBanksList()
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'BANKS_LIST_RECEIVED',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
        throw error
      })
  },
  getListWithCashAccounts (params) {
    // Request the payment accounts list to the API, then triggers the change on the store if success
    API.PaymentAccounts.ListWithCashAccounts(params)
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'PAYMENT_ACCOUNTS_LIST_RECEIVED',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
      })
  }
}
