import React, { Component, Fragment } from 'react'
import { Link } from 'react-router'
import { isWarningActivated } from 'utils'
import classNames from 'classnames'
import AppStorage from 'api/appStorage'
import API from 'api'
import SessionStore from 'stores/session'
import LogRocket from 'logrocket'
import Advertisment from 'shared/advertisment'
import pluralize from 'pluralize'

import DiagnosisStore from 'stores/diagnosis'
import SmartSyncStore from 'stores/smartsync'

import RegisterActions from 'actions/register'
import PlanActions from 'actions/plan'
import PlanStore from 'stores/plan'
import SmartSyncActions from 'actions/smartsync'

// Get app wide notifications every time the user opens the app

// Conditions:
// - Read from SessionStore if the user has a message
// - If the user has a message, check when was updated
// - If updated less than 6 hours ago, don't do anything
// - If updated more than 6 hours ago, check if there are new messages or the message should be deactivated
// - Expected message format:
// user-notification: {
//   message: 'Manda tu resumen de movimientos', // If this is null or empty string the message will be deactivated
//   link: '', // Absolute link to a path inside the app, if null or empty string the message won't have a link
//   type: 'alert'|'success'|'info'|'warning'
// }

const getCurrentState = () => {
  return {
    diagnosis: DiagnosisStore.getDiagnosis(),
    remainingDays: PlanStore.getRemainingDays(),
    hasToIntroduceToken: SmartSyncStore.getInfoToken()
  }
}

class AppWideNotification extends Component {
  state = {
    notifications: [],
    showAdvertisement: false,
    ...getCurrentState()
  }

  componentDidMount () {
    const currentEntity = SessionStore.getCurrentEntity()
    const { status, id, plan } = currentEntity
    DiagnosisStore.addChangeListener(this._onChange)
    PlanStore.addChangeListener(this._onChange)
    SmartSyncStore.addChangeListener(this._onChange)
    SmartSyncActions.tokenDefeated()
    this.getNotifications()
    this.logRocket()
    if (status === 'demo') {
      RegisterActions.askDiagnosisStatus(currentEntity.user_id)
    }
    if (plan === 'free_premium') {
      PlanActions.getPlanRemainingDays(id)
    }
  }

  componentWillUnmount () {
    DiagnosisStore.removeChangeListener(this._onChange)
    SmartSyncStore.removeChangeListener(this._onChange)
    RegisterActions.clearCurrentDiagnosis()
  }

  _onChange = () => {
    this.setState(getCurrentState())
  }

  logRocket () {
    const user = SessionStore.getUser()
    const userId = user.id.toString()
    LogRocket.identify(userId, {
      name: user.name,
      email: user.email
    })
  }

  getNotifications = async () => {
    const { notifications } = await API.User.GetNotifications()

    if (!AppStorage.hasNotifications()) AppStorage.initNotifications()
    AppStorage.setNotifications([...notifications])
    this.setState({ notifications })
  }

  renderNotification (notification, i) {
    const { accountingPeriod } = this.state

    if (!notification.link) {
      return (
        <div
          key={i}
          className={`columns app-wide-notifications--notification--info ${notification.type}`}
        >
          <div className='column is-narrow'>
            <i className='mdi mdi-calendar-clock icon' />
          </div>
          <div className='column is-10'>{notification.message}</div>
        </div>
      )
    }

    if (accountingPeriod) {
      return (
        <div
          key={i}
          className={`app-wide-notifications--notification ${notification.type}`}
        >
          <Link to={`/mi-contabilidad/${accountingPeriod.id}/ver`}>
            {notification.message}
          </Link>
        </div>
      )
    }
  }

  returnText = () => {
    return (
      <p className='app-wide-notifications--notification__text'>
        A partir del <strong>1 de abril</strong> Enconta cambia su número
        telefónico, ahora podrás contactarnos marcando{' '}
        <strong>800 479 5555</strong>.
      </p>
    )
  }

  link = step => {
    const currentEntity = SessionStore.getCurrentEntity()
    let src = ''
    switch (step) {
      case 'registry':
        src = `/diagnostico/credenciales/${currentEntity.rfc}`
        break
      case 'in_progress':
        src = '/diagnostico/en-proceso'
        break
      case 'done':
        src = '/diagnostico/listo'
        break
    }
    return src
  }

  diagnosisInProgress = () => {
    const { step } = this.state.diagnosis
    const nameStep = step === 'done' ? ' listo' : ' en proceso'
    return (
      <p className='app-wide-notifications--notification__text'>
        Tu diagnóstico está{nameStep},{' '}
        <Link
          to={this.link(step)}
          className='app-wide-notifications--notification__text__link'
        >
          da clic aquí
        </Link>{' '}
        para ver el estatus.
      </p>
    )
  }

  trialText = () => {
    const { remainingDays } = this.state
    return (
      <p>{`Te quedan ${remainingDays} ${pluralize(
        'días',
        remainingDays
      )} de plan premium gratis`}</p>
    )
  }

  token = () => {
    const bank = 'bancomer'
    return (
      <p className='app-wide-notifications--notification__text'>
        Por favor actualiza tu token de {bank} para poder descargar los
        movimientos bancarios del próximo mes, dando{' '}
        <Link
          to={`/${bank}/actualizar-token/`}
          className='app-wide-notifications--notification__text__link'
        >
          clic aquí
        </Link>{' '}
      </p>
    )
  }

  infoDiagnosis = () => {
    return <Advertisment text={this.diagnosisInProgress()} />
  }

  infoToken = () => {
    return <Advertisment text={this.token()} diagnosis />
  }

  renderTrialAdvertisement () {
    const { remainingDays } = this.state
    const color = remainingDays > 1 ? 'warning' : 'red'
    return <Advertisment text={this.trialText()} color={color} />
  }

  render () {
    const { notifications, hasToIntroduceToken } = this.state
    const warnig = isWarningActivated()
    const { showAdvertisement, remainingDays } = this.state
    const currentEntity = SessionStore.getCurrentEntity()
    const { status, plan } = currentEntity
    const { step } = this.state.diagnosis
    const isTrialAdvertisementShowed =
      plan === 'free_premium' && remainingDays <= 5
    return (
      <Fragment>
        {isTrialAdvertisementShowed && this.renderTrialAdvertisement()}
        {hasToIntroduceToken && this.infoToken()}
        {showAdvertisement && (
          <Advertisment
            text={this.returnText()}
            icon={'alert-circle-outline'}
            color={'yellow'}
          />
        )}
        {status === 'demo' &&
          (step === 'registry' || step === 'in_progress' || step === 'done') &&
          this.infoDiagnosis()}
        {notifications.length > 0 && (
          <div
            className={classNames('app-wide-notifications', {
              'app-wide-notifications--warning-active': warnig
            })}
          >
            {notifications.map((notification, i) =>
              this.renderNotification(notification, i)
            )}
          </div>
        )}
      </Fragment>
    )
  }
}

export default AppWideNotification
