// Libraries
import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

// Components
import TableCalculations from './TableCalculations'
import { formatCurrency } from 'utils'
import IconButton from 'shared/iconButton'

const CALCULATIONS_STATUS = {
  reviewed_by_accountant: 'Pendiente revisión',
  rejected: 'Rechazado',
  accepted: 'Aceptado'
}

export default class Calculation extends PureComponent {
  static propTypes = {
    isOpen: PropTypes.bool.isRequired,
    calculation: PropTypes.shape({
      id: PropTypes.number.isRequired,
      status: PropTypes.string.isRequired,
      isr_tax: PropTypes.string.isRequired,
      iva_tax: PropTypes.string.isRequired,
      total_to_pay: PropTypes.string.isRequired,
      created_at: PropTypes.string.isRequired
    }).isRequired,
    period: PropTypes.shape({
      month: PropTypes.number.isRequired,
      year: PropTypes.number.isRequired
    }).isRequired,
    toggleOpenCalculation: PropTypes.func.isRequired
  }

  render () {
    const {
      calculation,
      period,
      isOpen,
      toggleOpenCalculation,
      sendCalculation,
      showButtons,
      oldestPeriod
    } = this.props
    return (
      <div className='accordion accordion--dark-gray calculation-element'>
        <header className='accordion__header'>
          <div
            className='accordion__header__title is-fullwidth'
            onClick={toggleOpenCalculation(calculation.id)}
          >
            <div className='columns is-fullwidth'>
              <div className='column is-4'>
                <p>Cálculos previos</p>
              </div>
              <div className='column is-5'>
                <p className='accordion__header__title__status'>
                  Estatus:{' '}
                  <span
                    className={classNames('is-semibold', {
                      danger: calculation.status === 'rejected',
                      success: calculation.status === 'accepted'
                    })}
                  >
                    {CALCULATIONS_STATUS[calculation.status]}
                  </span>
                </p>
              </div>
              <div className='column is-3'>
                <p className='accordion__header__title__total'>
                  Por pagar:{' '}
                  <span>{formatCurrency(calculation.total_to_pay)}</span>
                </p>
              </div>
            </div>
          </div>
          <div className='accordion__header__icons'>
            <IconButton
              icon={isOpen ? 'menu-up' : 'menu-down'}
              onClick={toggleOpenCalculation(calculation.id)}
            />
          </div>
        </header>
        {isOpen && (
          <TableCalculations
            calculation={calculation}
            period={period}
            sendCalculation={sendCalculation}
            showButtons={showButtons}
            oldestPeriod={oldestPeriod}
          />
        )}
      </div>
    )
  }
}
