import React from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

import StatusIcon from 'shared/statusIcon'

const PendingPayment = props => (
  <span
    className={classNames('status-label', {
      'is-hidden': props.status !== 'pending_payment' && props.status !== 'all',
      'only-one': props.status !== 'all'
    })}
  >
    <StatusIcon className='warning' />
    Por {props.type === 'income' ? 'Cobrar' : 'Pagar'}
  </span>
)

PendingPayment.propTypes = {
  status: PropTypes.string,
  type: PropTypes.string
}

const PartiallyPaid = props => (
  <span
    className={classNames('status-label', {
      'is-hidden': props.status !== 'partially_paid' && props.status !== 'all',
      'only-one': props.status !== 'all'
    })}
  >
    <StatusIcon className='info' />
    {props.type === 'income' ? 'Cobrada' : 'Pagada'} parcialmente
  </span>
)

PartiallyPaid.propTypes = {
  status: PropTypes.string,
  type: PropTypes.string
}

const Paid = props => (
  <span
    className={classNames('status-label', {
      'is-hidden': props.status !== 'paid' && props.status !== 'all',
      'only-one': props.status !== 'all'
    })}
  >
    <StatusIcon className='success' />
    {props.type === 'income' ? 'Cobrada' : 'Pagada'}
  </span>
)

Paid.propTypes = {
  status: PropTypes.string,
  type: PropTypes.string
}

const Cancelled = props => (
  <span
    className={classNames('status-label', {
      'is-hidden': props.status !== 'cancelled' && props.status !== 'all',
      'only-one': props.status !== 'all'
    })}
  >
    <StatusIcon className='danger' />
    Cancelada
  </span>
)

Cancelled.propTypes = {
  status: PropTypes.string
}

const StatusLabel = props => (
  <div className='caption'>
    <PendingPayment {...props} />
    <PartiallyPaid {...props} />
    <Paid {...props} />
    <Cancelled {...props} />
  </div>
)

export default StatusLabel

StatusLabel.propTypes = {
  status: PropTypes.string,
  type: PropTypes.string
}

StatusLabel.defaultProps = {
  status: 'all',
  type: 'income'
}
