// Libraries
import React from 'react'
import accounting from 'accounting'

// Components
import TextField from 'shared/textField'
import { absolutify } from 'utils'

// Children
import SpecificDescriptions from './specificDescriptions'

const IDENTIFICATION_NUMBER_KEY = 'identification_number'
const CUSTOMS_AMOUNT_KEY = 'customs_amount'
const CUSTOMS_UNIT_KEY = 'customs_unit'
const CUSTOMS_UNIT_VALUE_KEY = 'customs_unit_value'
const USD_VALUE_KEY = 'usd_value'
const BRAND_KEY = 'brand'
const MODEL_KEY = 'model'
const SUB_MODEL_KEY = 'sub_model'
const SERIAL_NUMBER_KEY = 'serial_number'

const requiredFields = new Set()

class Merchandise extends React.Component {
  constructor (props) {
    super(props)
    this.data = {
      [IDENTIFICATION_NUMBER_KEY]: '',
      [CUSTOMS_AMOUNT_KEY]: '0',
      [CUSTOMS_UNIT_KEY]: '0',
      [CUSTOMS_UNIT_VALUE_KEY]: '0',
      [USD_VALUE_KEY]: '0',
      specific_descriptions: [
        {
          [BRAND_KEY]: 'brand',
          [MODEL_KEY]: '',
          [SUB_MODEL_KEY]: '',
          [SERIAL_NUMBER_KEY]: ''
        }
      ]
    }
    this.state = Object.assign({}, this.data)
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Updates an input's value when it changes
  onChange (field, e) {
    this.setState({
      [field]: e.target.value
    })
  }

  // Sets the correct format to the content field when it losses the focus
  formatField (field, kind = 'string') {
    const formatted = this.setFormat(this.state[field], kind)
    this.setState({
      [field]: formatted
    })
  }

  // Formats the current field as number when it gets the focus
  unformatField (field, kind = 'string') {
    const unformatted =
      kind === 'string'
        ? this.state[field]
        : accounting.unformat(this.state[field])
    this.setState({
      [field]: unformatted
    })
  }

  // Validates form
  validate () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate()) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  // Exports state data
  export (specificDescriptions = this.refs['specificDescriptions'].export()) {
    const response = {
      [IDENTIFICATION_NUMBER_KEY]: this.state[IDENTIFICATION_NUMBER_KEY],
      [CUSTOMS_AMOUNT_KEY]: this.state[CUSTOMS_AMOUNT_KEY],
      [CUSTOMS_UNIT_KEY]: this.state[CUSTOMS_UNIT_KEY],
      [CUSTOMS_UNIT_VALUE_KEY]: accounting.unformat(
        this.state[CUSTOMS_UNIT_VALUE_KEY]
      ),
      [USD_VALUE_KEY]: accounting.unformat(this.state[USD_VALUE_KEY])
    }
    return {
      ...response,
      specific_descriptions: Object.keys(specificDescriptions).map(
        key => specificDescriptions[key]
      )
    }
  }

  // Sets a specific format depending of the kind
  setFormat (value, kind = 'string') {
    switch (kind) {
      case 'money':
        return accounting.formatMoney(absolutify(value), '$ ')
      case 'integer':
        return absolutify(value).toString()
      case 'percentage':
        return accounting.formatMoney(absolutify(value), {
          symbol: '%',
          format: '%v %s'
        })
      case 'string':
        return value
      default:
        return value
    }
  }

  // Main render
  render () {
    return (
      <div>
        <hr />
        <p className='content'>
          <span className='title'>Mercancía</span>
        </p>
        <div className='columns'>
          <div className='column'>
            <TextField // identification_number, 1 a 100 caracteres alfanuméricos
              label='Número de identificación'
              placeholder='Número de identificación'
              value={this.state[IDENTIFICATION_NUMBER_KEY]}
              onChange={this.onChange.bind(this, IDENTIFICATION_NUMBER_KEY)}
              required
              ref={r => requiredFields.add(r)}
            />
            <br />
            <TextField // cantidad aduana       **Convertir a string**
              label='Cantidad de bienes en aduana'
              placeholder='Cantidad de bienes en aduana'
              value={this.state[CUSTOMS_AMOUNT_KEY]}
              onChange={this.onChange.bind(this, CUSTOMS_AMOUNT_KEY)}
              onBlur={this.formatField.bind(
                this,
                CUSTOMS_AMOUNT_KEY,
                'integer'
              )}
              onFocus={this.unformatField.bind(
                this,
                CUSTOMS_AMOUNT_KEY,
                'integer'
              )}
              required
              ref={r => requiredFields.add(r)}
            />
            <br />

            <TextField
              label='Unidad de medida'
              placeholder='Unidad de medida'
              value={this.state[CUSTOMS_UNIT_KEY]}
              onChange={this.onChange.bind(this, CUSTOMS_UNIT_KEY)}
              required
              ref={r => requiredFields.add(r)}
            />
            <br />
            <TextField // valor unitario aduana. Igual al valor declarado en USD
              label='Precio unitario de mercancía en aduana'
              placeholder='Precio unitario de mercancía en aduana'
              value={this.state[CUSTOMS_UNIT_VALUE_KEY]}
              onChange={this.onChange.bind(this, CUSTOMS_UNIT_VALUE_KEY)}
              onBlur={this.formatField.bind(
                this,
                CUSTOMS_UNIT_VALUE_KEY,
                'money'
              )}
              onFocus={this.unformatField.bind(
                this,
                CUSTOMS_UNIT_VALUE_KEY,
                'money'
              )}
              required
              ref={r => requiredFields.add(r)}
            />
            <br />
            <TextField // valor dólares. Valor total en dólares. Valor resultante de: tipo de cambio, tipo de cambio usd, cantidad, valor unitario.
              label='Valor total (USD)'
              placeholder='Valor total (USD)'
              value={this.state[USD_VALUE_KEY]}
              onChange={this.onChange.bind(this, USD_VALUE_KEY)}
              onBlur={this.formatField.bind(this, USD_VALUE_KEY, 'money')}
              onFocus={this.unformatField.bind(this, USD_VALUE_KEY, 'money')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
          <div className='column'>
            <SpecificDescriptions ref='specificDescriptions' />
          </div>
        </div>
      </div>
    )
  }
}

export default Merchandise
