import React, { Fragment } from 'react'
import Button from 'shared/button'
import { PLANS_SPECIFICATIONS } from './catalogsPlan'
import { handleURL, getClientsPlan, getPlanName } from 'utils'
import Tooltip from 'shared/tooltip'
import classNames from 'classnames'
import IconButton from 'shared/iconButton'
import { browserHistory } from 'react-router'

import RegisterActions from 'actions/register'
import NotificationsActions from 'actions/notifications'

import SatCredentialsStore from 'stores/satCredentials'
import SessionStore from 'stores/session'

const premiumPlan = (route, controlPlansModal) => {
  handleURL(route)
  controlPlansModal()
}

const buttonAction = (route, planName, controlPlansModal) => {
  const action =
    planName === 'Premium'
      ? premiumPlan(route, controlPlansModal)
      : handleDiagnosis()
  return action
}

const moveStep = () => {
  const currentEntity = SessionStore.getCurrentEntity()
  const { id } = currentEntity
  const step = {
    diagnosis: { step: 'in_progress' }
  }
  RegisterActions.setCurrentStep(id, step)
    .then(() => {
      browserHistory.push('/diagnostico/en-proceso')
    })
    .catch(error => {
      NotificationsActions.error(error)
    })
}

const handleDiagnosis = () => {
  const currentEntity = SessionStore.getCurrentEntity()
  const { rfc, id } = currentEntity
  const credentials = SatCredentialsStore.getCredentialStatus()
  const { has_ciec: hasCiec, has_fiel: hasFiel } = credentials
  RegisterActions.createDiagnosis(id)
    .then(() => {
      if (hasCiec || hasFiel) {
        moveStep()
      } else {
        browserHistory.push(`/diagnostico/credenciales/${rfc}`)
      }
    })
    .catch(() => {
      if (hasCiec || hasFiel) {
        browserHistory.push('/diagnostico/en-proceso')
      } else {
        browserHistory.push(`/diagnostico/credenciales/${rfc}`)
      }
    })
}

const Plans = props => {
  const { controlPlansModal } = props
  const plan = getClientsPlan()
  const planName = getPlanName()
  return (
    <Fragment>
      <p className='plans__title'>Elige el plan m√°s conveniente para ti</p>
      <p className='plans__subtitle'>Tu plan actual es {planName}</p>
      <div className='plans'>
        {PLANS_SPECIFICATIONS.map(
          (
            { title, buttonText, specifications, price, route, periodicity },
            index
          ) => {
            const disabledButton =
              title === 'Freemium' ||
              (title === 'Premium' && plan === 'premium')
            const style = disabledButton ? 'none' : 'primary'
            return (
              <div key={index} className={'plans__card--highlight'}>
                <div className='plans__card card-container'>
                  <p className='plans__card-title'>{title}</p>
                  <p className='plans__price'>{price}</p>
                  <p className='plans__periodicity'>{periodicity}</p>
                  <div className='plans__button-container'>
                    <Button
                      disabled={disabledButton}
                      buttonStyle={style}
                      className='plans__button'
                      onClick={() =>
                        buttonAction(route, title, controlPlansModal)
                      }
                    >
                      {buttonText}
                    </Button>
                  </div>
                  {specifications.map(({ specification, tooltip }, index) => {
                    return (
                      <div key={index} className='plans__container'>
                        <i className='mdi mdi-check plans__icon' />
                        <Tooltip tooltip={tooltip} className='plans__tooltip'>
                          <p
                            className={classNames({
                              plans__specification: tooltip
                            })}
                          >
                            {specification}
                            {tooltip && (
                              <IconButton
                                icon='comment-question'
                                className='plans__tooltip-icon'
                              />
                            )}
                          </p>
                        </Tooltip>
                      </div>
                    )
                  })}
                </div>
              </div>
            )
          }
        )}
      </div>
    </Fragment>
  )
}

export default Plans
